@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("forums.mozillazine.org") {

html, 
body {
	background: #010101 !important;
}

a {
	color: lightsteelblue !important;
	text-decoration: none !important;
}

strong, 
.signature, 
.postbody, 
.content, 
.display-options > label, 
.sidebar, 
#login label, 
.rightside, 
.details dt, 
#ucp dt, 
.lastpost,  
#report .panel label, 
#ucp label{
	color: #c9c9c9 !important;
}

a:hover {
	background-color: transparent !important;
	color: cornflowerblue !important;
}

.postlink {
	border-bottom-color: transparent !important;
}

img[src*="navHeader.gif"],
#gootop,
#goobot {
	display: none !important;
}

.navbar, 
.sidebar, 
.forabg, 
.forumbg {
	background: #333 !important;
	border-radius: 3px !important;
}

.corners-top, 
.corners-top>span, 
.corners-bottom, 
.corners-bottom > span {
	background: none !important
}

#sidebar {
	padding-top: 0 !important;
}

.row, 
.post.bg2, 
.panel.bg2, 
#login > .panel, 
div.panel.bg3, 
.activetab span, 
.activetab a, 
#report .panel, 
#post > .panel, 
#agreement > .panel, 
#memberlist .bg2 {
	background: #222 !important;
	border: 0 !important;
	color: #aaa !important
}

.row:nth-child(2n+1), 
.post.bg1, 
#postingbox,
.panel.bg1, 
#memberlist .bg1 {
	background: #111 !important;
}

#memberlist td {
    border: 0 !important;
}

.topics, 
.posts,
.views, 
.lastpost, 
.signature, 
.postprofile, 
.topiclist.cplist, 
.mark, 
#page-body > HR {
	border-color: #323232 !important
}

h3, 
e-body > HR, 
.codebox > dt, 
h2 {
	border-color: #121212 !important;
	color: #aaa !important
}

h3 + p, 
fieldset.quick-login label {
	color: #aaa !important;
}

.pagination > span > a {
	background: none !important;
	border: 0 !important;
}

.pagination > span > strong {
	background: #222 !important;
	border-radius: 2px !important;
}

.pagination > span > a:visited {
	color: #777 !important
}

#tabs li:not(.activetab) span, 
#tabs  li:not(.activetab) a, 
#message {
	background: #111 !important;
	color: #555 !important
}

#cp-main 
.panel, 
#active-subsection a, 
#cp-menu .cp-mini {
	background: #111 !important;
	color: #aaa !important
}

#navigation li:not(#active-subsection) a {
	background: none !important;
}

textarea#message.inputbox, 
.inputbox, 
blockquote, 
.codebox, 
#uploadfile input {
	-moz-appearance: none !important;
	background: rgba(0,0,0,.7) !important;
	border: 1px solid #444 !important;
	border-radius: 3px !important;
	outline: none !important;
	color: #aaa !important;
}

select, 
select:focus {
	-moz-appearance: none !important;
	background-image: url("data: image/png;base64,")!important;
	background-position: 99% !important;
	background-repeat: no-repeat !important;
	border-style: solid !important;
	border-width: 1px !important;
	border-color: #333 !important;
	border-radius: 3px !important;
	padding-right: 12px !important;
	background-color: #000 !important;
	color: #aaa !important;
}

select:focus > option:checked {
    background: #333 !important;
}

input[class*="button"] {
	-moz-appearance: none !important;
	color: #aaa !important;
	background: rgba(0,0,0,.5) !important;
	box-shadow: 0 0 4px rgba(161,161,161,.9) inset !important;
	border-radius: 4px !important;
	border: 1px solid transparent !important;
	text-align: center !important;
}

.profile-icons li, 
.buttons, 
#login .button2 {
	opacity: .5 !important; color:#000 !important;
}

.buttons:hover, 
.profile-icons li:hover,
#login .button2:hover {
	opacity: .9 !important; color: #000 !important;
}

div#options-quickreply label, 
dd, 
div#smiley-box, 
#post .fields2 label {
	color: #aaa !important
}

hr, 
dd.info {
	border-color: #444!important;
}

form[action="./search.php"]>.panel {
	background: #222 !important;
}

form[action="./search.php"]>.panel span {
	color: #aaa !important;
}

.inner, 
.jumpbox label {
	color: #bbb !important;
}

.sendemail {
    background-image: url("data:image/gif;base64,R0lGODlhEgASALMJAO7u7nd3d6qqqszMzFVVVd3d3YiIiLu7u////////wAAAAAAAAAAAAAAAAAAAAAAACH5BAEAAAkALAAAAAASABIAAARFMMlJq7046837OGAoioJ0IGiqqoaJhCtqBEibnLEaELWbxzbcj+VDjYS9m6rAbBKVqeMTOQQEcwBUFlCwCQzgsFgc6FgiADs=") !important;
