css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.tumblr.com/dashboard/notes/"), url-prefix('https://www.tumblr.com/dashboard/notes/')   {
body {
   margin: 10px 20px 20px !important;
   font-size: 0.9em !important;
   font-family: "Lucida Grande",Helvetica,sans-serif !important;
   background: #252a30 left top repeat-x !important;
   background-image: url("data:image/png;base64,") !important;
}
body:before {
   content: "Reblog Notes" !important;
   padding: 0 20px !important;
   font-size: 1.2em !important;
   font-weight: bold !important;
   color: #fff !important;
}
a {
   color: #71a4db !important;
   text-decoration: none !important;
}
a img {
   border: 0 none !important;
   vertical-align: middle !important;
}
ol {
   margin: 5px 0 0 !important;
   padding: 1em 1.3em 1.3em !important;
   border: solid #b3b8bf !important;
   border-width: 1px 2px !important;
   -moz-border-radius: 1.3em !important;
   list-style-type: none !important;
   color: #555 !important;
   background: #fff !important;
}
li {
   margin: 0 !important;
   border-bottom: 1px dotted #ccc !important;
   padding: 0.3em 5px !important;
}
li.source_post {
   background: #f5f5f5 !important;
}
blockquote {
   margin: 0.3em 0 0 22px !important;
   border-left: 4px solid #a8a8a8 !important;
   padding-left: 10px !important;
}
blockquote a {
   color: #a8a8a8 !important;
   font-size: 0.9em !important;
}
}
