css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("4chan.org") {

/* Cleanup */

	body>hr,
	img[src="http://static.4chan.org/image/partyhat.gif"],
	.yui-g,
	.warnicon,
	.doubledash,
	small,
	td.rules,
	#rules,
	.postarea .rules,
	.postarea small,
	td[style="padding: 4px 7px 12px;"],
	td[style="padding: 4px 7px 12px 7px;"],
	td[style="padding: 10px 7px 7px 7px;"],
	td[style="padding: 18px 7px 18px 7px;"],
	td[style="padding: 18px 7px;"],
	#recaptcha_logo,
	#recaptcha_tagline,
	.postarea .refreshBtn,
	body>br,
	.notice,
	.replymode,
	.postarea form>div,
	#footer,
	th,
	form table tbody>tr:nth-of-type(2) td[align="right"],
	html body span[style="left: 5px; position: absolute;"],
	html body>a,
	.deletebuttons input[type=button],
	form[name=delform]>hr:last-of-type,
	form[name=delform]>hr:nth-last-of-type(2),
	form[name=delform]>br:last-of-type,
	form[name=delform]>br[clear=left]:last-of-type,
	form[name=delform]>center {
		display: none !important;
	}
	
	form[name=delform]>table[align=right]:last-of-type,
	#backwash_tooltip {
		visibility: hidden;
	}

	* {
		font-family: "Calibri" !important;
		text-decoration: none !important;
	}

	form[name="delform"] {
		padding: 0 250px 0 0 !important;
		margin: 0 !important;
		bottom: 23px !important;
		top: 19px !important;
		right: 0 !important;
		left: 0px !important;
		overflow-y: scroll;
		overflow-x: auto;
		position: absolute !important;
	}
	
	hr {
		border: none !important;
		border-top: 0 transparent solid;
		padding: 0 !important;
		height: 0;
		width: 100% !important;
	}
	
	#header,
	#header>#navtop,
	.pages {
		font: 12px Calibri !important;
		text-align: center !important;
		font-size: 0 !important;
		color: transparent !important;
		width: auto !important;
		border-bottom: 1px solid !important;
	}

	#header>#navtop {
		position: static !important;
		width: 100% !important;
	}
	
	#header,
	.pages {
		position: fixed;
		top: 0 !important;
		left: 0 !important;
		right: 0 !important;
		padding: 0 0px 0 2px !important;
		z-index: 1
	}
	
	.pages {
		top: auto !important;
		bottom: 0 !important;
		border: 0 !important;
		width: 100% !important;
		background-color: transparent !important;
	}
	
	#header a, .pages a, .pages input {
		display: inline-block;
		font-size: 12px !important;
		height: 14px !important;
		text-align: center;
		margin: 0 1px;
		min-width: 14px;
		border: 0 none !important;
		padding: 0 !important;
		background: none transparent !important;
	}
	
	#header a {
		padding: 0 0 0 1px !important;
	}
	
	.pages b {
		display: inline-block;
		font-size: 12px !important;
		min-width: 14px;
		text-align: center;
		margin: 0 1px;
	}

/* Post Form */

	.postarea form:not(#newform), .postarea table, .postarea tbody {
		max-width: 245px !important;
		min-width: 245px !important;
		width: 245px;
		padding: 0 !important;
		border-spacing: 0px !important;
	}

	.postarea form:first-child {
		position: fixed !important;
		z-index: 2;
		margin: 0 !important;
		padding: 0 !important;
		bottom: 65px !important;
		right: 22px !important;
	}

	#com_submit,
	.postarea input[type=file] > input[type=button],
	.postarea input[name="upfile"],
	.postarea input[type=submit] {
		font: 200 8pt Calibri, "Verdana" !important;
	}
	
	.postarea .postblock {
		border: none !important;
		background-color: transparent !important;
		font-weight: 200 !important;
		text-align: right !important;
		vertical-align: bottom;
		padding-bottom: 12px !important;
	}
	
	.postarea .postblock b {
		position: fixed;
		font: 9px Calibri !important;
		color: rgba(0,0,0,0.6) !important;
		text-transform: uppercase;
		z-index: 2;
	}
	
	.postarea tr:nth-of-type(1) .postblock b:after {
		content: "#TRIPCODE##SECURETRIP";
	}
	
	.postarea tr:nth-of-type(2) .postblock b:after {
		content: " + NOKO + SAGE";
	}
	
	.postarea tr:nth-of-type(1) .postblock b,
	.postarea tr:nth-of-type(2) .postblock b,
	.postarea tr:nth-of-type(4) .postblock b,
	.postarea tr:nth-of-type(5) .postblock b,
	.postarea tr:nth-of-type(7) .postblock b {right: 22px;}

	.postarea tr:nth-of-type(3) .postblock b,
	.postarea tr:nth-of-type(6) .postblock b {right: 74px;}
	
	.postarea input,
	.postarea input[type=submit],
	.postarea textarea,
	.postarea .recaptcha_image_cell>center>#recaptcha_image,
	.postarea .recaptchatable #recaptcha_response_field {
		-moz-appearance: none;
		-webkit-appearance: none;
		border: 1px solid #888 !important;
	}

	.postarea input[type=text],
	.postarea input[type=password],
	.postarea input[type=file][name="upfile"] > input[type=text],
	.postarea input[type=file] > input[type=button],
	.postarea textarea.inputtext	{
		width: 248px !important;
		padding: 1px 0 1px 0 !important;
		z-index: 1;
	}

	.postarea input[type=text],
	.postarea input[type=password],
	.postarea input[type=file][name="upfile"] > input[type=text],
	.postarea input[type=file] > input[type=button] {
		height: 21px !important;
	}

	.postarea input[type=file] {
		z-index: 1;
	}

	body .postarea input[name="sub"],
	.postarea input[type=file][name="upfile"] > input[type=text] {
		width: 196px !important;
		margin: 0 2px 0 0 !important;
		vertical-align: middle;
	}

	.postarea #com_submit,
	.postarea input[type=file] > input[type=button],
	.postarea input[type=submit] {
		width: 50px !important;
		height: 19px !important;
		margin: 0 0 0 0 !important;
		padding: 0 !important;
		vertical-align: middle;
	}
	
	.postarea input[type="file"] {
		-moz-appearance: none;
		background: transparent;
		border: none !important;
		height: 21px !important;
		padding: 0 !important;
	}

	.postarea textarea.inputtext,
	textarea#message {
		font-size: 10pt !important;
		resize: none;
	}

	.postarea textarea#message {
		width: 248px !important
	}

	.postarea td, .reply td {
		z-index: 0;
		padding: 0 !important;
	}

/* Captcha */

	.postarea td.recaptcha_image_cell,
	td[style="padding-left: 7px;"] {
		padding: 0 !important;
	}

	.postarea .recaptcha_image_cell>center>#recaptcha_image,
	.postarea #recaptcha_image img {
		width: 246px !important;
		height: auto !important;
	}

	.postarea .recaptcha_input_area {
		padding: 0px 0 0 0 !important;
	}
	.postarea .recaptcha_image_cell>center {
		width: 245px !important;
		text-align: left !important;
	}

	.postarea .recaptcha_image_cell>center,
	.postarea .recaptcha_image_cell,
	.postarea tr[height="73"], tr[height="73"] td {
		height: auto !important;
	}

/* Spoiler Image Checkbox */

	div.postarea>form tr:nth-of-type(3)>td:nth-of-type(3) {
		font-size: 0 !important;
		color: transparent;
	}

	div.postarea>form td:nth-of-type(3)>label {
		position: fixed;
		top: auto;
		font-size: 12px !important;
		color: #888;
		position: fixed;
		bottom: 83px;
		right: 97px;
		vertical-align: bottom;
	}

	div.postarea>form td:nth-of-type(3)>label input,
	form[name="delform"]>div:not(.thread) input,
	.deletebuttons input[type=checkbox] {
		vertical-align: middle;
	}

/* Posts */

	form[name=delform]>table,
	div.thread>table {
		width: 100%;
	}
	
	.omittedposts {
		clear: both;
		padding-left: 20px;
		display: block !important;
	}
	
	td.reply,
	td.replyhl {
		border: 0 !important;
	}

/* Addons and such */

	#navtopr {
		position: fixed;
		right: 33px !important;
		left: auto !important;
		top: 2px !important;
	}

	form[name="delform"]>div:not(.thread) {
		position: fixed;
		top: 0px;
		right: 83px;
		width: 180px;
		font: 12px Calibri !important;
		z-index: 4;
	}
	
	form[name="delform"]>div:not(.thread) label {
		float: left !important;
	}
	
	form[name="delform"]>div:not(.thread) select {
		margin: 0 0 0 0 !important;
		float: right !important;
	}
	
	#backwash_tipcell {
		max-width: 50%;
		visibility: visible;
	}
	
	#backwash_tipcell {
		visibility: visible;
		position: fixed;
		top: auto !important;
		right: 18px !important;
		bottom: 28px !important;
		left: auto !important;
		border: 0 !important;
	}

	.deletebuttons {
		visibility: visible;
		position: fixed;
		top: auto !important;
		right: 18px !important;
		bottom: 28px !important;
		left: auto !important;
		border: 0 !important;
	}	

	#backwash_tipcell {
		z-index: 5;
	}
	
	#backwash_tipcell img {
		max-width: 300px;
		height: auto !important;
	}
	
	.deletebuttons br {
		display: inline !important;
	}
	
	.navlinks {
		margin-right: 250px;
	}
	
	#options {
		position: fixed !important;
		top: 0 !important;
		bottom: 0 !important;
		right: 0 !important;
		left: 0 !important;
		border: 0 !important;
		z-index: 99 !important;
		text-align: center !important;
		vertical-align: middle !important;	
	}
	
	#options .move {
		text-decoration: underline !important;
		font-size: 16px !important;
	}
	
	#options .move a {
		position: fixed;
		top: 0;
		right: 5px;
		font-size: 20px !important;
	}
	
	#thread_filter, #filter {
		width: 250px;
		padding: 3px 0;
		top: auto !important;
		right: 16px !important;
		bottom: 0 !important;
		left: auto !important;
		border: 0 !important;

		-moz-border-radius: 0;
		-webkit-border-radius: 0;

		border-radius: 0;
		font: 12px Calibri !important;
		z-index: 4 !important;
	}
	
	#thread_filter>div:first-child  {
		cursor: default !important;	
	}
	
	#updater {
		right: 2px !important;
		left: auto !important;
		top: 2px !important;
		bottom: auto !important;
		border: 0 !important;
		font-size: 12px;
		z-index: 3 !important;
	}
	
	#updater .error {
		position: fixed;
		top: 0;
		right: 20px;
		font-size: 48pt;
	}

	#watcher {
		width: 248px !important;
		position: fixed !important;
		padding: 1px 0;
		top: 21px !important;
		right: 18px !important;
		bottom: auto !important;
		left: auto !important;
		border: 0 !important;
		border-bottom: 1px solid #000 !important;
		-moz-border-radius: 0;
		-webkit-border-radius: 0;
		border-radius: 0;
		font: 12px Calibri !important;
		overflow: hidden;
	}
	
	#watcher .move {
		padding: 2px 3px !important;
		margin-bottom: 2px !important;
		border-bottom: 1px solid #000 !important;
		text-decoration: none !important;
	}
	
	#watcher .move,
	#updater .move,
	#options .move {
		cursor: default !important;
	}

/* Adblock */

	img[src*="http://static.4chan.org/support/"],
	img[src*="http://static.4chan.org/image/title"]{
		display: none;
	}

/* Favicon Fix */

	img[src*="http://static.4chan.org/image/favicon"] {
		display: inline;
	}

/*FUCK YEAH COLORS*/

	* {
			background: #171717 !important;
			color:#ffffff !important;
	}
	
	.reply,
	.reply>*,
	.reply>*>*,
	.reply>*>*>* {
		background-color:#232323 !important;
	}

	.reply>tr>td {
		border: 1px solid #4f4f4f !important;
	}

	a {
		color:#88bbff !important;
	}
	
a:hover {
		color: #3377ff !important;
		text-decoration:underline !important;
	}
	
	.logo {
		position: fixed !important;
		right: 20px !important;
		bottom: 340px !important;
		z-index: 1;
		width: 246px !important;
	}

	.filetitle,
	.replytitle {
		color: #ff6666 !important;
	}

	.postername,
	.commentpostername {
		color: #aaaaaa !important;
	}

	.postertrip {
		color: #cccccc !important;
		font-weight: bold !important;
	}

	.reply>tr>.reply {

	}
	
	.unkfunc{	
		color:#7f9f7f !important;
	}

	/*fixing the problems*/

	select {
		color: #000000 !important;
	}

	.deletebuttons>input[type="submit"], input[type="text"], input[type="password"], input:not([type="checkbox"]){
		-moz-appearance: none;
		-webkit-appearance: none;
		border: 1px solid #888 !important;
		font: 200 8pt Calibri,"Verdana" !important;
		height: 19px !important;
	}

/*Form Fixes*/

	.postarea tr:nth-of-type(1) .postblock b,
	.postarea tr:nth-of-type(2) .postblock b,
	.postarea tr:nth-of-type(4) .postblock b,
	.postarea tr:nth-of-type(5) .postblock b,
	.postarea tr:nth-of-type(7) .postblock b,
	.postarea tr:nth-of-type(3) .postblock b,
	.postarea tr:nth-of-type(6) .postblock b,
	label {
		background: none !important;
		color: #efefef !important;
	}
	#thread_filter, #filter {
		padding:0!important
	}

	select,
	input[type=checkbox] {
		-moz-appearance: none;
		-webkit-appearance: none;
		border: none 0 !important;
		background-color: #171717 !important;
		color:#efefef !important;
		border: 1px solid #888888 !important;
	}
	select>*{
		border: 0!important;
	}


	form[name="delform"]>div:not(.thread) select:focus > button {
		visibility: hidden !important;
	}

}
	
@-moz-document domain(rs.4chan.org) {
	.postarea {
		margin-top: 20px !important;
	}
	.logo{
		display:none;	
	}
	.postarea form:first-child,
	.postarea .postblock,
	.postarea .postblock b {
		position: static !important;
	}
	
	td.postblock {
		border-color: #888888 !important;
	}
	.postarea tr:nth-of-type(1) .postblock b:after {
		content: " ";
	}

	body table table {
		width: 100% !important;
	}
}
