/* By Euda  9/17/2011 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("minecraftwiki.net") {

/* MAIN BACKGROUND GREY */
#content,
.vectorTabs li {
   background-color: #606060 !important;
   color: #d8d8d8 !important; }
/* BACKGROUND / A BIT DARKER */
body, #mw-head,
#toc, .toc, .mw-warning,
.thumbinner,
.gallery, .gallery td, .catlinks,
table,
#bodyContent table,
#bodyContent table td,
#bodyContent div[style*="background-color: #fcfcfc"] {
   background-color: #505050 !important;
   background-image: none !important;
   color: #ddd !important; }
/* BACKGROUND EVEN A BIT DARKER */
.vectorTabs li:hover, pre {
   background-color: #404040 !important;
   color: #ddd !important; }
/* OTHER CORE ELEMENT TWEAKS */
#contentSub, #contentSub2 { color: #ddd !important}
a, .collapsed h5,
.vectorTabs li a, 
.vectorTabs li a span { 
   color: #A7D7F9!important } /* 72A0CF C8D9EC */
a:hover { color:#E3F071!important }
a:active { color:#FFFF00!important }
a:visited { color:#A8B8C8!important }
h1, h2, h3, h4, h5, h6 { 
   color: #8BC4FF!important } /* 83A9D0 B0C9E4 */

/* HEADER TABS */
.vectorTabs ul,
.vectorTabs li, 
.vectorTabs li a,
#p-namespaces, #p-views {
  background-image: none !important }
.vectorTabs li.selected {
  background-color: #A0BFDE !important }
.vectorTabs li.selected a span { 
  color: black!important }
#mw-head-base {
  border-bottom: 2px solid #A7D7F9  !important }
div.vectorTabs { padding-left: 0px !important }
.vectorTabs li {
  border-left: 1px solid #A7D7F9 !important }
.vectorTabs ul {
  border-top: 1px solid #A7D7F9 !important;
  border-right: 1px solid #A7D7F9 !important }

.thumb { border: none !important }
.gallery td { border: 2px solid #888 !important }


/* WIKITABLE & NAVBOX SAME STYLE KTHX */
.wikitable,
.navbox,
td.navbox-group,
table.navbox-subgroup {
  background-color: #505050 !important;
  color: #e8e8e8 !important; }
.navbox { border: 1px solid #AAA !important }
.wikitable th,
th.navbox-title,
th.navbox-title .navbar,
th.navbox-title .navbar a span {
   background-color: #404040 !important }
td.navbox-group {
   background-color: #707070 !important; }
.navbox table td {
   border-color: #606060 !important;
   border-right: 1px !important;;
   border-bottom: 1px !Important; }
.navbox a { color: #9CBBDA!important }
.navbox-group a { color: #B0C9E4!important }

.mcwiki-header { color: black !important }
.mcwiki-header,
#bodyContent div[style*="background-color: #fcfcfc"] {
  border-color: #999 #888 #777 #999 !important;
  border-style: solid !important; }

pre { border: 1px dashed #A7D7F9!important }


/* SEARCH */
#simpleSearch {
  background-color: #ccc !important;
  background-image: none !important;
  border: 2px solid #999 !important; }
#simpleSearch label {
  color: #808080 !important; 
  padding: 0 0 0 0.25em !important;
  font-weight: 100!important; 
  font-size: 115%!important;
  top: 0 !Important;}

/* RIGHT PANEL remove & adjust rest */
#bodyContent2.en { margin-right: 0 !important }
#curse-panel { display: none !important; }


/* v1.1 TWEAKS AND FIXES, RANDOM SHIT */
.alternatecells:nth-child(2n+1) {background-color:#707070!important}


}
