css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("underverse.su") {

#logo, #ad-200 > A > IMG, #page_footer, .disclaimer, #counters, TD[width="40%"][valign="top"][nowrap="nowrap"] {
   display: none !important; }
#page_header {
   position: fixed !important;
   top: 0 !important;
   z-index: 999 !important;
   background: transparent !important; }
.topmenu.no-new-pm {
   border-bottom: 2px black solid !important; 
   border-left: 2px black solid !important;
   border-right: 2px black solid !important;
   border-top: 2px black solid !important;
   padding-right: 10px !important; }
HTML {
   overflow-x: hidden !important;
   padding-top: 50px !important; }
}
