css:
@-moz-document url-prefix(http://ylilauta.fi/) {
    a[href*="clk.tradedoubler.com"], a[href*="kumpaa.com"], div[style="float: left; width: 335px; height: 300px;"], a[href*="voimaravinto.com"], iframe

[src*="http://naamapalmu.fi/"], DIV[style="float: right;"]  {
                    display: none !important;
    }
     
    a, a:visited, a:hover, a:active, a:link,
    .reflink a, .reflink a:hover,
    #header a, #header a:hover,
    #navbot a, #navbot a:hover,
    #navbotr a, #navbotr a:hover,
    .pages td a, .pages td a:hover, .pages td b,
    .threadhead a, .threadhead,  a.quotejs:hover {
    color:#EEE !important;
    }
     
    html, body {
    background: #2C2C2C !important;
    color: #EEEEEE !important;
    }
     
    element.style {
    color: #07F !important;
    }
     
    td.reply {
    border:1px solid #555555 !important;
    border-left:none;
    border-top:none;
    padding:2px;
    }
     
    .doubledash {
    color:#555555;
    }
     
    .inputtext {
    margin:0;
    margin-right:2px;
    padding:1px 4px;
    border:1px solid #aaa;
    outline:none;
    font-family:arial,helvetica,sans-serif;
    font-size:10pt;
    }
     
    td>.inputtext {
    height:auto;
    padding-top:2px;
    padding-bottom:3px;
    }
     
    .navbar,
    .adminbar {
            color: #EFEFEF;
            font-size: 13px;
            line-height: 1.42em;   
    }
     
    .navbar a,
    .adminbar a {
            color: #EFEFEF;
            text-decoration: none;
    }
     
    .navbar a:visited,
    .adminbar a:visited {
            color: #EFEFEF;
    }
     
     
    hr {
    border-top:1px solid #555555 !important;
    }
     
    .postblock, ::selection, .reflinkpreview  {
    background:#444444 !important;
    border:1px solid #444444 !important;
    color: #aaa !important;
    }
     
     
    .rotating {
    background:#333333;
    border-right:1px solid #555555;
    border-bottom:1px solid #555555;
    }
     
    .pages {
    border:none;
    background:#333333;
    border-right:1px solid #555555;
    border-bottom:1px solid #555555;
    }
     
     
    .deletebuttons {
    text-align:right;
    }
     
    .deletebuttons br {
    display:none;
    }
     
     
    a.quotelink {
    color:#000080;
    }
     
    .logo {
    clear:both;
    text-align:center;
    font-size:24pt;
    color:#EEEEEE !important;
    width:100%;
    }
     
     
    .unkfunc {
    color:#789922;
    }
     
     
     
    .filetitle,.replytitle {
    background:inherit;
    color:#CC1105;
    font-weight:g800;
    }
     
    .postername,.commentpostername {
    background:inherit;
    color:#117743 !important;
    font-weight:800;
    }
     
    .postertrip {
    background:inherit;
    color:#228854;
    }
     
    .oldpost {
    background:inherit;
    color:#f00000;
    font-weight:800;
    }
     
    .omittedposts,.abbr {
    color:#707070;
    }
     
    .reply {
    background:#333333 !important;
    color:#EEEEEE !important;
    max-height: 350px;
    overflow: auto;
    }
     
    .replyhl {
    background:#F0C0B0;
    color:#EEEEEE;
    }
     
     
    #header,#navbot,#navbotr {
    font-size:9pt;
    color:#b86;
    }
     
     
     
    #footer {
    clear:both;
    padding-top:10px;
    }
     
    .highlight {
            background:#333333;
            color:#EEEEEE;
            border: 2px dashed #EEAA88;
    }
    .replymode {
            background:#E04000;
            text-align:center;
            padding:2px;
            color:#FFFFFF;
            width:100%;
    }
    .catalogmode {
            background:#0040E0;
            text-align:center;
            padding:2px;
            color:#FFFFFF;
            width:100%;
    }

}
