@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("plus.google.com") {

.Rh, .c-cb-V-xe, .Fo, .il, .zla,  .yla, .xla, .c-i-cH-V, .c-cH-V, .c-Va-V, .c-i-Va-V, img[src="//ssl.gstatic.com/s2/oz/images/google-logo-plus-0fbe8f0119f4a902429a5991af5db563.png"] {
display: none !important;
}
#gbw, #gb2, #gbx3, #gbx4 {
border: none !important;
position: fixed !important;
top: -500px !important;
}
.c-i-Qa-V {
position: fixed !important;
left: 0px !important;
}
.c-zh-Wf {
position: fixed !important;
left: 0px !important;
bottom: 0px !important;
opacity: 0.3 !important;
border: none !important;
-moz-box-shadow: none !important;
}
.c-zh-Wf:hover {
opacity: 1 !important;
}

#gb {
background: none !important;
}
#gbg {
visibility: visible !important;
position: fixed !important;
top: 0px !important;
}
#gbtc {
visibility: visible !important;
position: fixed !important;
top: 0px !important;
}
.gbzt-hvr, .gbgt-hvr, .gbzt:focus, .gbgt:focus {
background-color: transparent !important;
}
.c-Yd-V-xe {
position: relative !important;
bottom: 500px !important;
}

.c-cb-V, .c-i-cb-V, .c-cb-V-Fi {
background: rgba(241, 241, 241, 0.9) none !important;
position: fixed !important;
top: 0px !important;
height: 30px !important;
z-index: 99;
-moz-box-shadow: 10px 0px 15px #333 !important;
}
.c-cb-dg-V {
position: fixed !important;
top: 0px !important;
left: -120px !important;
}
#contentPane {
width: 750px !important;

}
#contentPane::after {
content: url('http://i.imgur.com/raqo3.png') !important;
position: fixed !important;
top: 2px !important;
left: 13px !important;
visibility: visible !important;
opacity: 100 !important;
z-index: 999 !important;
}
#content #contentPane > div:first-child > div:first-child.jx.Sv,
#content #contentPane > div:first-child > div:first-child div:first-child.jx.Sv,
#content #contentPane > div:nth-child(2) > div:first-child > div:first-child.jx.Sv ,
#content #contentPane > div:nth-child(3) > div:first-child > div:first-child.jx.Sv {
	margin-left: 0 !important;
	margin-top: 20px !important;
	margin-bottom: 2px !important;
	width: 100% !important;
	border-radius: 4px !important;
	text-align: center !important;
	letter-spacing: 4px;
	-webkit-transition: text-shadow 1s ease-in-out 0;
border: 1px solid #ddd !important;
border-left: none !important;
border-right: none !important;
}
}
