css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.goha.ru") {
.body_border {
    border: 1px solid #fff !important;
}
a:hover {color: #ce2200 !important;text-decoration:underline!important;}

.alt1, .alt1Active, .vbmenu_control, .thead, .tcat, .alt3, .tfoot, .alt2 {
    background-color:#f4f4f6!important;
    color: #494949!important;
    background-image: none!important;
}
.thead, tborder {border: #6b6464 !important;}
.smallfont {
    color: #a5a5a5!important;
}
td#banner, #searchbar_top, a#logo, #threadslist tbody:nth-child(1){
    display: none !important;
}   
body div[style="text-align: center; margin: 3px;"] {
    display: none !important;
}
body div[style="text-align: center; margin-top: 3px; margin-left: 32px;"] {
    display: none !important;
}
body form[action="http://forums.goha.ru/index.php"] {
    display: none !important;
}
body td[align="left"] {
    display: none !important;
}
}
