css:
@-moz-document domain("steamgifts.com") {
p, .discussions .details .title, .discussions .details .title a {
  color: #fff !important;
}

html, body, ul, ol, li, img, h1, h2, h3, h4, h5, h6, form, fieldset, .post, .sgpRowPage, .row, .content, .notification
{
  background: none transparent !important;
  box-shadow: none !important;
  color: #ff7 !important;
  text-shadow: none;
  outline: none;
  text-decoration: none;
  font-style: none;
}

body {
  font-family: Calibri, Helvetica, Arial, sans-serif;
}

#nav {
  background: #000 !important;
  border-radius: 15px 15px 15px 15px;
  color: #fff;
}

.sub_navigation {
  background: #222;
  color: #fff;
  border: 1px solid #444;
}

.post {
  border-top: 1px solid #444;
  border-bottom: 1px solid #444;
  background: #222;
}

.pagination {
  background: #333;
  color: #fff;
  border: 1px solid #444 !important;
}

.bg_gradient, .content, .div, div {
  background: transparent;
  color: #fff;
}

#nav .left .search .input input, #nav .left .search, #nav .left .search.disabled, a.sale.left, a.sale.right {
  background: #444;
  color: #fff;
  border: none;
  border-radius: 15px 15px 15px 15px;
}

#nav .left ol li a, #nav .left ol li a {
  color: #fff;
}

.wrapper {
  background: #000 !important;
  color: #fff;
}

.post .left .description, .post .left .description .time_remaining, .post .left .description .created_by, .post .left .description .created_by a, .discussions .details
{
  background: #333;
  color: #fff;
}

.post .left .title, .post .left .title a
{
  color: #aaa;
  text-decoration: none;
  outline: none;
  font-family: Calibri, Arial, Times New Roman;
  text-shadow: none;
}

.post .left .entries a
{
  color: #aaa;
  font-style: none;
  text-decoration: none;
  font-size: 115%;
}

.recent_winners .row .details, .top_contributors .row .details
{
  background: transparent;
  color: #fff;
}

.recent_winners .row.first, .top_contributors .row.first
{
  background: #666;
  color: #fff;
  border: 1px solid #444;
}

.recent_winners .row, .top_contributors .row
{
  background: #333;
  color: #fff;
  border: 1px solid #444;
}

.top_contributors .row .gift_value
{
  background: transparent;
  color: #ff7;
  text-decoration: none;
  font-style: none;
  outline: none;
  text-shadow: none;
}

.top_contributors .row .details .gift_count strong, .recent_winners a, .top_contributors a, .recent_winners .row .title a, p.title
{
  color: #fff;
}

.footer
{
  background:#111 !important;
  border-radius: 15px 15px 15px 15px;
}

.footer, .footer .footer_column
{
  background: transparent;
}

.footer .footer_column h4, .footer .footer_column ul li, .footer .footer_column ul li a
{
  text-decoration: none;
  outline: none;
  text-shadow: none;
  color: #fff;
}

.footer .footer_column ul
{
  border: none;
}

.featured, .featured .left .featured_img, .featured .left .featured_img, .featured .left .private_img
{d
  background: transparent;
}

.bg_gradient
{
  background: #000 !important;
}

.pagination .numbers a.selected
{
  color: #f00;
  text-decoration: none;
}

.pagination .numbers a
{
  color: #aaf;
  text-decoration: none;
}

#nav .left ol li a, #nav .left ol li a
{
  opacity: 1.0 !important;
}

.discussions .body, .comment.child .child_container
{
  background: #222;
  color: #ff7;
  border: 1px solid #444;
  border-radius: 15px 15px 15px 15px;
}

.comment_body textarea
{
  background: #444;
  color: #ff7;
}

.discussions .details .title, .discussions .details .title a
{
  text-decoration: none;
  text-shadow: none;
  outline: none;
}

.discussions .row
{
  border-top: 1px solid #444;
  border-bottom: 1px solid #444;
  background: #222;
}

.sub_navigation a.button
{
  background: #444;
  color: #fff;
}

.discussions .body, .comment.child .child_container
{
  border-left: 1px solid #444;
  border-right: 1px solid #444;
}

.register .input input
{
  background: #444;
  color: #ff7;
}

.invites .row .code, .invites .heading
{
  text-decoration: none;
  outline: none;
  text-shadow: none;
  color: #fff;
}

.no_results, .content .create_giveaway .notification, .create_giveaway .input select, .create_giveaway .input input, .date_description, .create_giveaway .input select, .create_giveaway .input input, .create_giveaway .input textarea {
  background: #333;
  color: #ff7;
  border: 1px solid #444;
  text-decoration: none;
  outline: none;
  text-shadow: none;
}

.register .input .notification
{
  border-top: 1px solid #444 !important;
  border-bottom: 1px solid #444 !important;
  border-left: none !important;
  border-right: none !important;
}

.register .input .verify.success 
{
  background: #333;
  border-top: 1px solid #444;
  border-bottom: 1px solid #444;
  border-left: none;
  border-right: none;
  text-decoration: none;
  outline: none;
  text-shadow: none;
}

.date_description strong
{
  color: #ff7;
}
.html, html {
background: #000;
}

.create_giveaway .divider
{
  border-top: 1px solid #444;
  border-bottom: 1px solid #444;
}

.post.fade
{
 opacity: 0.5;
}

.post .right
{
background: #333;
border: 1px solid #444;
border-radius: 5px 5px 5px 5px;
}

div#visualization
{
background: #333 !important;
}

#stats hr {
  border: 1px solid #444;
}

div#gafPopup {
background: #333 !important;
border: 1px solid #444 !important;
color: #ff7 !important;
}

input, textarea, keygen, select, button, isindex
{
  background: #444;
  color: #ff7;
}

a {
  color: #ff7;
}

.register .important, .invites .row.first, .invites .divider, .invites .row
{
  background: #333;
  text-shadow: none;
  outline: none;
  color: #fff;
  border-top: 1px solid #444;
  border-bottom: 1px solid #444;
}

.ui-slider-horizontal
{
  
  background: #333;
  border: 1px solid #444;
}

.ui-slider-horizontal .ui-slider-handle
{
  background: #666;
  border: 1px solid #555;
}

.markdown p, p, a {
  text-shadow: none;
  outline: none;
}

.featured, .profile {
  background: #222;
  border: 1px solid #444;
  border-radius: 15px 15px 15px 15px;
}

.comment.parent .body_container .comment_reply, .comment.parent .body_container .comment_reply a 
{
  color: #0f0;
  outline: none;
  text-shadow: none;
}

.comment.parent
{
  background: #222;
  border-radius: 15px 15px 15px 15px;
  border: 1px solid #444 !important;
}

.comment_container, .parent_container
{
  border: none;
}

.discussions .row.borderless
{
  border: 1px solid #444;
  border-radius: 15px 15px 15px 15px;
}
}
