/*******************************/
/* Santiago Wanderers skin     */
/* Fecha: 18/09/2011           */
/* Diseñador: LleyoGraphics    */
/*******************************/



@-moz-document domain("hattrick.org") {





#top

{

	height: 0px !important;

	position: relative !important;

	z-index: 50 !important;

        background: #000000 !important;

        /*background: url(page/top.png) bottom left no-repeat !important;*/

}



#footer a

{

        color: white !important; 

}



#hattrick,#hattrickNoSupporter 

{

        margin:auto!important; 

        position:relative;

        left:-5px;width:993px!important;

}



#ticker

{

	position: absolute !important;

	top: 25px !important;

	left: 15px !important;

        width: 240px !important;

}





#ticker div a

{

	padding-bottom: 2px !important;

	color: #ffffff !important;

	text-decoration: none !important;

	line-height: 1.1em !important;

}



#shortcuts

{

	margin-top: 0px !important;

	right: 5px !important;

}



#teamLinks

{

        color: #ffffff !important;

        margin-top: 20px !important;

	margin-left: 0px !important;

}



div#teamLinks a

{

        color: #ffffff !important;

}



/*Users online - week - date - time*/



div[id="online"] 

{

        color: #ffffff !important; 

        float:right!important;

        margin-right:0pt!important;

        margin-top:10pt!important;

}

div[id="time"]

{

        color: #ffffff !important;

        float:right!important;

        margin-right:-8pt!important;

        margin-top:10pt!important;

}



/*Foxtrick View by ID*/





#ft_rapidid, #ft_rapidid a

{

    margin-top: 13px !important;

    margin-right: 15px !important;

    color: #ffffff !important;

}



/*Page background*/

body

{  

  background: #005b15 url(http://i158.photobucket.com/albums/t110/Nice-Bullanguero/fondo3wanderers-1.jpg) top right no-repeat fixed !important;

}



/*centre title*/

h1 

{

        color: #00721a !important;

        border-bottom: solid thin darkgrey !important;

        background: #eeeeee !important;

}



/*all boxHead*/

h2 

{

        /*color: white !important;*/

        /*border-bottom: solid thin #eeeeee !important;*/

        /*background: #c70000 !important;*/

}



/*centre news*/

.boxBody h2 

{ 

        color: #005b15 !important;

        border-bottom: solid 1px black !important;

}





/*-----------boxHeads-----------*/



/*Left menu*/

.subMenuBox .boxHead

{

	background: url(http://i158.photobucket.com/albums/t110/Nice-Bullanguero/cabeceraswander.gif) !important;

}



.subMenuBox .boxHead .boxLeft

{

        background: url(http://i158.photobucket.com/albums/t110/Nice-Bullanguero/cabeceraswander.gif) !important;      

}



/*Centre box*/

.main .boxHead h2 

{

	background: url(http://i158.photobucket.com/albums/t110/Nice-Bullanguero/cabeceraswander.gif) !important;  

}



/*Right menu*/

.sidebarBox h2 

{

	background: url(http://i158.photobucket.com/albums/t110/Nice-Bullanguero/cabeceraswander.gif) !important;

}



/* Press, GB, Team events */

.main .mainBox h2 

{

	border-bottom: solid 1px #000000 !important;

	background-color: #fff !important;

	color: #005b15 !important;

	font-size: 14px !important;

	font-weight: bold !important;

	padding: 1px !important;

}





/*Central header paths*/



h2 a 

{

        color: #ffffff !important;

}



h2 a:hover 

{

        color:#005b15 !important;

}





/*News and team events date*/



.date

{

	color: #000000 !important;

	padding-right: 8px;

	font-weight: 400;

}



/*Left Menu titles*/

h3 

{

        color: #005b15 !important;

        border-bottom: solid thin #d9d9d9 !important;

        background: #eeeeee !important;

}



/*General Links*/

a

{

        color: #005b15 !important;

	text-decoration: none !important;

}



/*----subMenu and sidebar links----*/

.subMenuBox a, .subMenuBox a, .sidebarBox a 

{

	text-decoration: none !important;

	color: black !important;

}



/*Bot teams link*/



div a[class="shy"] 

{

        color: gray !important;

}





/*Hide Hattrick logo*/

#logo

{

        background: none !important;

}



#stageLogo {

        background: none !important;

}



/*Hide ticker's stars*/

#ticker div

{

        /*background: none !important;*/

}





/* central page background */

#content

{

        background-image: none !important;

        background-color: #ffffff !important;



}



/*Image header (banner)*/

#header 

{

        background: url(http://i158.photobucket.com/albums/t110/Nice-Bullanguero/bannerwander.jpg) !important;

	height: 125px !important;

	padding-top: 50px !important;

}



/*--------Top menu settings--------*/

#menu

{

	height: 30px !important;

        width: 975px !important;	

        margin-top: 5px !important;

        margin-left: -14px !important;

        bottom: 0px !important;

        background: none !important;

}



#menu a

{

	line-height: 20px !important;  

        color: #005b15 !important;

        /*-moz-border-radius: 2% !important;*/        

        /*background: #d9d9d9 !important;*/

}



div#menu a:hover 

{

        color:#4ec369 !important; background: none !important;

        line-height: 20px !important;

}



a:hover

{

	color: #000000 !important;

}



/*bottom white line*/

div[id="bottom"]

{

        background: #ffffff !important;

}





/*---------Forum---------*/

.cfHeader

{

        background:  url(http://i158.photobucket.com/albums/t110/Nice-Bullanguero/cabeceraswander.gif) !important;

}



#cfPollHeader

{

	background: url(http://i158.photobucket.com/albums/t110/Nice-Bullanguero/cabeceraswander.gif) !important;

	padding: 5px 10px;

	font-weight: bold;

	color: #ffffff;

}



.cfWrapper

{

	border: solid 1px #000000 !important;

	margin: 0 0 12px 0;

}



.cfWrapper div.signature a, div.signature-trunc a, div.signature-expand a {

    color: #999999 !important;

    text-decoration: underline !important;

}



.cfHeader a

{

	color: #ffffff !important;

	font-weight: bold;

	text-decoration: none;

}



.cfWrapper a

{

	text-decoration: none;

        /*color: white !important;*/

}



.cfUserInfo a

{

        /*color: #de4747 !important;*/

}



.cfMessage a

{

	/*color: #de4747 !important;*/

}



.folderHeader a:link 

{

	color: #005b15 !important;

	font-weight: bold !important;

}



.cfFooter a

{ 

        color: black !important;

}





.folderItem div.ignore

{

        cursor: pointer;

        width: 10px;

        height: 10px;

        background: url(http://i253.photobucket.com/albums/hh75/reiben85/cross_small.png) no-repeat !important;

}



.folderItem div.unignore

{

        cursor: pointer;

        width: 10px;

        height: 10px;

        background: url(http://i253.photobucket.com/albums/hh75/reiben85/tick_small.png) !important;

}



.folderItem div.unignore:hover

{

        background: url(http://i253.photobucket.com/albums/hh75/reiben85/tickGreen_small.png) !important;

}



/*Ads section*/



div[class="insertHeader"]

{

        background: url(http://i158.photobucket.com/albums/t110/Nice-Bullanguero/cabeceraswander.gif) !important;

}



div[class="insertFooter"]

{

        background: #ffffff !important;

}





/*shortcuts*/



img.scNewMail 

{

	background: transparent url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/Newmail.png) no-repeat !important;

}





/* MatchLineup */

.name a

{

	color: #277f31 !important;

}



/* Tabs Match orders*/

.ui-tabs .ui-tabs-nav li a { float: left; text-decoration: none; padding: 4px 10px 3px 10px; color:#eee; text-decoration:none;  color: white !important;}



/* Tabs Match line up */

.LinkTabActive, .LinkTabNotActive {

    background-color: #2E741B;

    color: #FFFFFF !important;

    float: left;

    font-size: 1em;

    font-weight: 700;

    padding: 0 4px;

    text-decoration: none;

}









/*Foxtrick TeamPopupLinks*/



ul.ft-popup-list 

{

    color: #000000 !important;

    background-color: #fff !important;

    border: solid 1px #000000 !important;

}



ul.ft-popup-list a

{

    /*color: #000000 !important;*/

}





ul.ft-popup-list > li > a, ul.ft-popup-list > li > span {

    color: #000000 !important;

    cursor: pointer;

    display: block !important;

    font-weight: normal !important;

    padding: 0 !important;

    text-align: left;

    text-decoration: underline !important;

}

ul.ft-popup-list > li:hover {

    background-color: #C3E7C7 !important;

}





div[class*="ad_"]

{

        display:none !important;

}



}
