css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
url(http://bungie.net/),
url-prefix(http://www.bungie.net/),
url-prefix(http://flawless.bungie.net/),
url-prefix(http://admin.bungie.net/),
url-prefix(http://seventhcolumn.bungie.net/),
url-prefix(http://halo.bungie.net/),
url-prefix(http://bungie.net/),
url-prefix(https://www.bungie.net/),
url-prefix(https://flawless.bungie.net/),
url-prefix(https://admin.bungie.net/),
url-prefix(https://seventhcolumn.bungie.net/),
url-prefix(https://halo.bungie.net/),
url-prefix(https://bungie.net/)
{

html {background: url("data:image/jpeg;base64,%3D%3D") repeat-x fixed left top #093E60 ! important;}

div.sContent-head {background: url("data:image/jpeg;base64,%3D") ! important; border-bottom: 3px solid #000000 ! important; float: left ! important; height: 99px ! important; position: relative ! important; width: 100% ! important; z-index: 11 ! important;}

body > div:first-child {background: none ! important;}

body {background: none ! important;}

div.bgRepeat {background: url("data:image/png;base64,%3D") repeat-y scroll center center transparent ! important;}

/* more navbar stuff */
.mainNav li a em {background: url("data:image/png;base64,%3D%3D") ! important;}
.mainNav li a.Projects em {background-position: 0px -14px ! important; height: 14px ! important; width: 52px ! important;}
.mainNav li a.Stats em {background-position: 0px -28px ! important; height: 14px ! important; width: 48px ! important;}
.mainNav li a.About em {background-position: 0px 0px ! important; height: 14px ! important; width: 72px ! important;}
.mainNav li a.Community em {background-position: 0px -42px ! important; height: 14px ! important; width: 80px ! important;}
.mainNav li a.Account em {background-position: -0px -56px ! important; height: 14px ! important; width: 59px ! important;}
}

/* image pages */
@-moz-document
url(http://bungie.net/),
url-prefix(http://www.bungie.net/images),
url-prefix(http://flawless.bungie.net/images),
url-prefix(http://admin.bungie.net/images),
url-prefix(http://seventhcolumn.bungie.net/images),
url-prefix(http://halo.bungie.net/images),
url-prefix(http://bungie.net/images),
url-prefix(https://www.bungie.net/images),
url-prefix(https://flawless.bungie.net/images),
url-prefix(https://admin.bungie.net/images),
url-prefix(https://seventhcolumn.bungie.net/images),
url-prefix(https://halo.bungie.net/images),
url-prefix(https://bungie.net/images)
{
body {background: none ! important;}
html {background: none ! important;}
}
