css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("0taku.livedoor.biz") {

/*他サイト更新情報*/
#top_rss,#middle_rss,#bottom_rss{
    display:none !important;
}

/*テキスト広告*/
div.body>span[style="color:#FF0000"],
div.body>hr,
div.google-user-ad,
a[href^="http://ac.ebis.ne.jp/"]{
    display:none !important;
}

/*トップページの右カラム*/
#right{
    display:none !important;
}
#main_col{
    width:auto !important;
    margin-right:10px !important;
}
#left{
    margin-left:-1020px !important;
}

/*トップページの左カラム広告*/
.ad_wrap,.left_col_ad,.plugin-memo{
    display:none !important;
}

/*記事上広告*/
#header_ad,
#tweet_ad{
    display:none !important;
}

/*記事内広告*/
#articletop_ad{
    display:none !important;
}

/*記事下広告*/
div#articleBodyMore_bottom>ul,
div.entry_ad,
div[class="amazon clearfix"],
#google_ad,
#amazonrankbottom_ad,
#article>div>h3,
#article>div>h4,
#pagenavtop_ad,
#pagenavbottom_ad,
.comment_ad,.reverse_access{
    display:none !important;
}

}

@-moz-document url-prefix("http://0taku.livedoor.biz/archives/") {

/*左カラム広告*/
div#left,
#top_amazon_wrap2,
div#right_col>iframe{
    display:none !important;
}

}
