css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://dogfood.zimbra.com/zimbra/"), url-prefix("https://catfood.zimbra.com/zimbra/") {
.ZToolbarButtonBorder, .ZButtonBorder, .ZAppTabBorder {
    border:0 !important;
    background-color:transparent !important;
    font-weight:inherit;
}
/* Button in default, unselected, unpressed state. */
.ZToolbarButton, .DwtToolbarButton, .ZButton, .ZSelect  {
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#fefefe), to(#e6e6e6));
    background-image: -moz-linear-gradient(top, #fefefe, #e6e6e6);
    border:1px solid #949494;
    border-radius: 3px !important;
    padding:1px 2px;
    margin:0 2px;
    text-align:center;
}

/* mouse-over button */
.ZToolbarButton.ZHover, .DwtToolbarButton.ZHover, .ZButton.ZHover, .ZSelect.ZHover  {
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#c7e9ff), to(#90b6ff));
    background-image: -moz-linear-gradient(top, #c7e9ff, #90b6ff);
    border:1px solid #6767a3;
}


/* Button is pressed. */
.ZToolbarButton.ZActive, .DwtToolbarButton.ZActive, .ZButton.ZActive , .ZSelect.ZActive {
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#90b6ff), to(#c7e9ff));
    background-image: -moz-linear-gradient(top, #90b6ff, #c7e9ff);
    border:1px solid #6767a3;
}

/* Button is disabled. */
.ZToolbarButton.ZDisabled, .DwtToolbarButton.ZDisabled, .ZButton.ZDisabled, .ZSelect.ZDisabled {
    opacity:.5;
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#fefefe), to(#e6e6e6));
    background-image: -moz-linear-gradient(top, #fefefe, #e6e6e6);
    border:1px solid #949494;
}
.ZButton.ZAppTab {
    border-radius: 3px 3px 0 0 !important;
    border:1px solid #949494;
    border-bottom:0;
}
.ZAppTabBorder {
    color:#666666;
    padding-top:1px;
}
.ZAppTab.ZSelected {
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#e6e6e6), to(#fefefe));
    background-image: -moz-linear-gradient(top, #e6e6e6, #fefefe);
    border:1px solid #666666;
    border-bottom:0;
}
.ZAppTab.ZSelected .ZAppTabBorder {
    black;
}
/* Toolbars */
.ZToolbarTable .vertSep {
    display:none;
}
td#ztb_search_searchMenuButton .ZButton {
    border-radius: 11px 0 0 11px !important;
    position:relative !important;
    left:3px;
}
#zb__Search__MENU_left_icon {
    padding-top:2px !important;
}
#zb__Search__MENU_left_icon .ImgMessage {
    position:relative;
    left:5px;
}
#skin_spacing_top_row #zb__Search__SAVE, #skin_spacing_top_row #CHECK_MAIL {
    background:transparent !important;
    border:0 !important;
}
}
