css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://w3schools.invisionzone.com")  {

html[xmlns] { /* Check for the xmlns attribute so it does not affect html elements within the post editor iframes */
   background-color: rgba(149,149,149,1) !important;
}
body {
   background: -moz-linear-gradient(top, rgba(19,19,19,1) 0%, rgba(26,26,26,1) 20%, rgba(40,40,40,1) 26%, rgba(10,10,10,1) 47%, rgba(1,1,1,1) 50%, rgba(13,13,13,1) 54%, rgba(149,149,149,1) 100%) !important;
   padding-bottom: 5px !important;
}

a {
   color: #000222 !important;
}
a:hover {
   color: #222666 !important;
   text-decoration: underline !important;
}

#header_bar{
   background: -moz-linear-gradient(center bottom, #1B1B1B 11%, #333333 45%) !important;
}

#content {
   background-color: #999 !important;
   box-shadow: 5px 7px 5px rgba(36,36,36,.6) !important;
   color: #333 !important;
   border-radius: 5px;
}

.desc, .ipsType_pagedesc {
   color: #444 !important;
}
.breadcrumb, .breadcrumb a {
   color: #1B1B1B !important;
}
.row2 {
   background-color: #EEE !important;
}

.message {
   background: -moz-linear-gradient(top, rgba(165,210,146,1) 0%, rgba(133,187,115,1) 44%, rgba(68,160,60,1) 100%) !important;
   border-radius: 3px;
   border-color: #6E886E !important;
}
.message.error {
   background: -moz-linear-gradient(top, rgba(239,189,196,1) 0%, rgba(230,168,173,1) 44%, rgba(226,136,128,1) 100%) !important;
   border-color: #C3548D !important;
   color: #60000C !important;
}
.general_box {
   border-top-left-radius: 4px !important;
   border-top-right-radius: 4px !important;
}
.general_box h3 {
   background: -moz-linear-gradient(top, rgba(0,0,0,1) 0%, rgba(52,52,52,1) 21%, rgba(98,98,98,1) 49%, rgba(61,61,61,1) 80%, rgba(0,0,0,1) 100%) !important;
   color: #DEDEDE !important;
   font-weight: bold !important;
}

.boxShadow {
   box-shadow: 6px 12px 12px rgba(0, 0, 0, 0.7) !important;
}

#board_stats .value {
   background-color: rgba(120,120,120,.4) !important;
   color: #3A3A3A !important;
}

#backtotop:hover {
   background:  -moz-radial-gradient(center, #1B1B1B 40%, #999999 85%) !important;
}
#copyright a, #copyright a:hover {
   color: #CCC !important;
   text-shadow: 2px 2px 1px #555;
   text-decoration: none !important;
}

#ajax_loading {
   background: url(data:image/gif;base64,) center no-repeat, -moz-linear-gradient(top, rgba(41,143,233,1) 0%, rgba(21,120,207,1) 21%, rgba(5,88,161,1) 50%, rgba(24,135,232,1) 76%, rgba(97,170,239,1) 100%) top left !important;
   border-color: #232387 !important;
}
#ajax_loading img {
   visibility: hidden !important;
}

/* 
 * ========================================
 * User Toolbar
 * ========================================
 */

#user_navigation #register_link {
   background-color: #555 !important;
   border-color: #333 !important;
   border-radius: 3px !important;
}

#user_navigation a {
   color: #FFF !important;
}
#user_navigation ul {
   padding-top: 3px !important;
}
#inbox_link, #notify_link, #user_link {
   border-radius: 8px;
   height: 15px !important;
   line-height: 15px !important;
}
#user_link {
   padding: 11px 12px !important;
   vertical-align: middle;
}
#inbox_link:hover, #notify_link:hover, #user_link:hover {
   background-color: #444 !important;
   text-decoration: none !important;
}
#user_link.menu_active:hover, #notify_link.menu_active:hover, #inbox_link.menu_active:hover {
   background-color: #FFF !important;
}
#user_link.menu_active, #notify_link.menu_active, #inbox_link.menu_active {
   color: #333 !important;
}
.ipsHasNotifications {
   background-color: #4040DF !important;
}

/* 
 * ========================================
 * Logo
 * ========================================
 */

#branding {
   background: -moz-linear-gradient(center bottom, #616161 11%, #1B1B1B 45%) !important;
   border-color: #444 !important;
}
#logo a {
   display: inline-block;
   background: url(data:image/png;base64,) no-repeat !important;
   background-size: contain !important;
   position: relative;
   bottom: 16px;
}
#logo a img {
   visibility: hidden !important
}

/* 
 * ========================================
 * Search Bar
 * ========================================
 */

#search_wrap {
   background-color: transparent !important;
   border: 1px solid #666666;
}
#main_search {
   color: #888888;
}
#search_options {
   padding: 0px 23px 0px 6px !important;
   background: url(data:image/png;base64,) no-repeat right #EAEAEA !important;
}
#search_wrap .submit_input {
   background: url(data:image/png;base64,) no-repeat !important;
   border: 1px solid #ACACAC !important;
}

/* Advanced Search */
#main_search_form .search_app.active {
   background-color: transparent !important;
   box-shadow: inset 0px 0px 3px #333, 0px 0px 3px #333 !important;
   border-color: #333 !important;
   color: #333 !important
}

/* 
 * ========================================
 * Primary Navigation
 * ========================================
 */

#primary_nav {
   background: -moz-linear-gradient(top, rgba(76,76,76,1) 0%, rgba(89,89,89,1) 5%, rgba(124,124,124,1) 12%, rgba(71,71,71,1) 19%, rgba(44,44,44,1) 24%, rgba(0,0,0,1) 26%, rgba(17,17,17,1) 43%, rgba(43,43,43,1) 68%, rgba(28,28,28,1) 88%, rgba(19,19,19,1) 100%) !important; 
   padding-top: 12px !important;
}
#primary_nav li a {
   border-top-left-radius: 4px;
   border-top-right-radius: 4px;
   padding: 4px 8px !important;
   color: #E0E0E0 !important;
   background-color: #333 !important;
   border: 1px solid #555;
   border-bottom: none;
}
#primary_nav li a:hover {
   background-color: #555 !important;
   text-decoration: none !important;
}
#primary_nav li.active a {
   color: #161616 !important;
   background-color: #999 !important;
}

#quickNavLaunch span {
   background: url(data:image/png;base64,) !important;
}

/* 
 * ========================================
 * Pagination Links
 * ========================================
 */

.pagination a:hover {
   text-decoration: none !important;
   color: #111 !important;
}
.pagination .pages a {
   border: 1px solid transparent;
}
.pagination .pages a:hover {
   background: -moz-linear-gradient(top, rgba(121,153,216,0.15) 0%, rgba(55,108,214,0.15) 23%, rgba(30,30,216,0.15) 100%) !important;
   border: 1px solid rgba(21,21,159,0.3) !important;
}
.pagination .pages li.active {
   background: -moz-linear-gradient(top, rgba(121,153,216,1) 0%, rgba(55,108,214,1) 23%, rgba(30,30,216,1) 100%) !important;
   border: 1px solid #15159F !important;
}
.pagination .pages .pagejump a, .pagination .back a, .pagination .forward a {
   background: #BBB !important;
   border: 1px solid #888 !important;
}
.pagination .pages .pagejump a:hover, .pagination .back a:hover, .pagination .forward a:hover {
   box-shadow: inset 0px 2px 4px rgba(245,245,245,.5) !important;
}

/* 
 * ========================================
 * Sidebar - Activity Feeds
 * ========================================
 */

.ipsSideBlock {
   background: #EEE !important;
}
.ipsSideBlock, .ipsSideBlock h3 {
   border-top-left-radius: 4px;
   border-top-right-radius: 4px;
}
.ipsSideBlock h3 {
   background: -moz-linear-gradient(top, rgba(86,86,86,1) 0%, rgba(0,0,0,1) 100%) transparent !important;
   color: #DEDEDE !important;
}
#toggle_sidebar, .ipsSideBlock h3 .mod_links, .ipsSideBlock h3 .mod_links:hover {
   color: #DEDEDE !important;
   text-decoration: none !important;
}

/* 
 * ========================================
 * Sidebar - Menus
 * ========================================
 */

.ipsSideMenu ul li {
   border-color: #DEDEDE !important;
}
.ipsSideMenu ul li.active a, .ipsSideMenu ul li.active a:hover {
   background-color: #999 !important;
}
.ipsSideMenu ul li a:hover {
   color: #000222 !important;
   text-decoration: none !important;
   background-color: #DDD !important;
}

/* 
 * ========================================
 * Popups
 * ========================================
 */

.popupWrapper {
   background-color: rgba(55,55,55,.7) !important;
   box-shadow: 6px 8px 12px rgba(0, 0, 0, 0.7) !important;
   padding: 5px !important;
}
.popupInner {
   border-top-left-radius: 4px !important;
   border-top-right-radius: 4px !important;
}

#ipsGlobalNotification .popupWrapper .popupInner {
   border-radius: 4px !important;
}
#ipsGlobalNotification .popupWrapper .popupInner .ipsPad {
   background: -moz-linear-gradient(top, rgba(221,221,221,1) 0%, rgba(201,201,201,1) 21%, rgba(172,172,172,1) 49%, rgba(202,202,202,1) 80%, rgba(221,221,221,1) 100%) !important;
   color: #222 !important;
}

.stem {
   background: none !important;
   height: 0px !important;
   width: 0px !important;
}
.stem.topleft, .stem.topright, .stem.bottomleft, .stem.bottomright {
   border-left: 16px solid transparent !important;
   border-right: 16px solid transparent !important;
}
.stem.topleft, .stem.topright {
   border-top: 17px solid rgba(80, 80, 80, 0.6) !important;
}
.stem.bottomleft, .stem.bottomright {
   border-bottom: 17px solid rgba(80, 80, 80, 0.6) !important;
}

.popupInner h3 {
   background: -moz-linear-gradient(top, rgba(27,27,27,1) 0%, rgba(56,56,56,1) 13%, rgba(78,78,78,1) 24%, rgba(10,10,10,1) 47%, rgba(1,1,1,1) 50%, rgba(13,13,13,1) 54%, rgba(149,149,149,1) 100%) !important;
   border-style: none !important;
   box-shadow: none !important;
}
.userpopup h3 a, .userpopup h3 a:hover {
   color: #DEDEDE !important;
}

.ipbmenu_content, .ipb_autocomplete {
   background-color: #F7F7F7 !important;
   border-color: #D5D5D5 !important;
}

.cke_skin_ips .cke_dialog_body {
   box-shadow: 6px 12px 12px rgba(0, 0, 0, 0.7) !important;
}
.cke_skin_ips .cke_dialog_title {
   background: -moz-linear-gradient(top, rgba(27,27,27,1) 0%, rgba(56,56,56,1) 13%, rgba(78,78,78,1) 24%, rgba(10,10,10,1) 47%, rgba(1,1,1,1) 50%, rgba(13,13,13,1) 54%, rgba(149,149,149,1) 100%) !important;
   border-top-left-radius: 4px !important;
   border-top-right-radius: 4px !important;
}
.cke_skin_ips .cke_dialog_contents {
   border: none !important;
}

/* 
 * ========================================
 * Buttons
 * ========================================
 */

.ipsButton_secondary:hover {
   border-color: #8A8A8A !important;
   text-decoration: none !important;
   color: #000222 !important;
}

.topic_buttons li.important a, .topic_buttons li.important span, .ipsButton .important, .topic_buttons li a, .topic_buttons li span, .ipsButton,
.topic_buttons li.important a:hover, .topic_buttons li.important span:hover, .ipsButton .important:hover, .topic_buttons li a:hover, .topic_buttons li span:hover, .ipsButton:hover {
   color: #DEDEDE !important;
   text-decoration: none !important;
}

.input_submit.alt {
   background-color: #E2E2E2 !important;
   border-color: #DADADA !important;
}

.ipsLikeButton.ipsLikeButton_enabled, .ipsLikeButton.ipsLikeButton_enabled:hover {
   color: #000222 !important;
   text-decoration: none !important;
}

.topic_buttons li.non_button a {
   color: #000222 !important;
}
.topic_buttons li.non_button a:hover {
   color: #222666 !important;
   text-decoration: underline !important;
}

.topic_buttons li.non_button a[title='Mark this forum as read'] {
   width: 12px !important;
   overflow: visible !important;
   margin-right: 130px !important;
   white-space: nowrap !important;
}
.topic_buttons li.non_button a[title='Mark this forum as read'] img {
   background-color: #666 !important;
   border: 1px solid #333 !important;
   border-radius: 4px !important;
   box-shadow: inset 0px 1px 1px #888 !important;
   padding: 4px 3px !important;
   vertical-align: middle !important;
}
.topic_buttons li.non_button a[title='Mark this forum as read']:hover {
   text-decoration: none !important;
   color: #000222 !important;
   cursor: default !important;
}
.topic_buttons li.non_button a[title='Mark this forum as read'] img:hover {
   box-shadow: inset 0px -1px 1px #222 !important;
   cursor: pointer !important;
}

.topic_buttons li.disabled a, .topic_buttons li.disabled span {
   color: #555 !important;
}

/* 
 * ========================================
 * Forums - Global
 * ========================================
 */

.maintitle {
   background: -moz-linear-gradient(top, rgba(27,27,27,1) 0%, rgba(56,56,56,1) 13%, rgba(78,78,78,1) 24%, rgba(10,10,10,1) 47%, rgba(1,1,1,1) 50%, rgba(13,13,13,1) 54%, rgba(149,149,149,1) 100%) !important;
   color: #DEDEDE !important;
   border-style: none !important;
   box-shadow: 0 1px 0 #777 inset !important;
}
.maintitle a {
   color: #DEDEDE !important;
}
.maintitle .toggle, .collapsed .toggle {
   border-radius: 14px;
}
.maintitle .toggle {
   background: url(data:image/png;base64,) !important;
}
.collapsed .toggle {
   background: url(data:image/png;base64,) !important;
}

.ipsBox {
   background: #CCC !important;
}
.ipsBox_container {
   background: #F7F7F7 !important;
   border: 2px inset #999 !important;
}
.unread {
   background-color: #FFF !important;
}
.unread .altrow, .unread.altrow {
    background-color: #EEE !important;
}
table.ipb_table {
   background-color: #F7F7F7 !important;
}
table.ipb_table td {
   border-color: #EAEAEA !important;
}
.expander:hover {
   text-decoration: none !important;
}

#announcements {
   border: 2px ridge #999 !important;
}
#announcements tr {
   background-color: #DEDEDE !important;
}
#announcements td {
   border-color: #BBB !important;
}

.ipsFilterbar li a {
   text-decoration: none !important;
}
.ipsFilterbar li.active a {
   background-color: transparent !important;
   text-shadow: 0px 0px 2px #FFFFFF, 0px 2px 4px #FFFFFF, 2px 2px 4px #FFFFFF, 2px 0px 4px #FFFFFF, 2px -2px 4px #FFFFFF, 0px -2px 4px #FFFFFF, -2px -2px 4px #FFFFFF, -2px 0px 4px #FFFFFF !important;
   box-shadow: none !important;
   color: #222 !important;
}

.ipsBadge_orange {
   background: -moz-linear-gradient(top, rgba(255,214,94,1) 0%, rgba(255,179,48,1) 27%, rgba(252,136,5,1) 100%) !important;
   box-shadow: 1px 1px 0px #CD9022 !important;
   border: 1px solid #D19628 !important;
   color: #863505 !important;
}
.ipsBadge_green {
   background: -moz-linear-gradient(top, rgba(121,153,216,1) 0%, rgba(55,108,214,1) 23%, rgba(30,30,216,1) 100%) !important;
   box-shadow: 1px 1px 0px #10109A !important;
   border: 1px solid #15159F !important;
}
.ipsBadge_grey {
   background: -moz-linear-gradient(top, rgba(195,207,216,1) 0%, rgba(152,162,170,1) 26%, rgba(82,97,104,1) 100%) !important;
   box-shadow: 1px 1px 0px #222 !important;
   border: 1px solid #333 !important;
}
.ipsBadge_lightgrey {
   background: -moz-linear-gradient(top, rgba(230,237,242,1) 0%, rgba(199,204,207,1) 26%, rgba(164,178,184,1) 100%) !important;
   box-shadow: 1px 1px 0px #666 !important;
   border: 1px solid #777 !important;
   color: #222 !important;
}
.ipsBadge_red {
   background: -moz-linear-gradient(top, rgba(201,0,40,1) 0%, rgba(143,2,34,1) 39%, rgba(109,0,25,1) 100%) !important;
   box-shadow: 1px 1px 0px #3A0205 !important;
   border: 1px solid #4A0510 !important;
}
.ipsBadge_purple {
   background: -moz-linear-gradient(top, rgba(203,123,229,1) 0%, rgba(147,32,201,1) 29%, rgba(114,30,153,1) 100%) !important;
   box-shadow: 1px 1px 0px #3A0545 !important;
   border: 1px solid #5D1263 !important;
}

/* 
 * ========================================
 * Forums - Post View
 * ========================================
 */

.post_wrap .author_info .user_details .basic_info .member_title {
   margin-top: 18px !important;
}
.post_wrap .post_body .posted_info {
   position: absolute !important;
   top: 10px;
   left: 10px;
}
.topic_summary .post_wrap .post_body .posted_info {
   text-align: right !important;
   padding-right: 5px !important;
   position: relative !important;
   top: -41px !important;
}
.topic_summary .post {
   margin-top: -41px !important;
}
.post_wrap .row2 .author.vcard {
   position: relative !important;
   top: 35px;
   display: block !important;
   width: 155px !important;
   text-align: center !important;
}

.post_block {
   background: -moz-linear-gradient(top, rgba(40,40,40,0.08) 0%, rgba(0,0,0,0) 100%) 0px 36px no-repeat, #FFF !important;
   background-size: 100% 30px, 100% 100% !important;
}
.post_block h3 {
   background: -moz-linear-gradient(top, rgba(210,210,210,1) 0%, rgba(204,204,204,1) 22%, rgba(194,194,194,1) 30%, rgba(236,236,236,1) 100%) !important;
}

.signature {
   color: #909090 !important;
}

/* 
 * ========================================
 * Forums - Post Editor
 * ========================================
 */

.cke_skin_ips .cke_wrapper, .cke_top {
   background: #BBB !important;
}
.cke_skin_ips .cke_wrapper {
   border-color: #DDD !important;
   box-shadow: 0px 1px 2px rgba(255,255,255,0.7) !important;
}
.cke_skin_ips a:hover {
   text-decoration: none !important;
}
.cke_skin_ips .cke_separator {
   border-left: 1px solid #999 !important;
}
.cke_skin_ips .cke_button a:hover, .cke_skin_ips .cke_button a:focus, .cke_skin_ips .cke_button a:active {
   background-color: #EEE !important;
}
.cke_skin_ips .cke_button a.cke_on:hover, .cke_skin_ips .cke_button a.cke_on:focus, .cke_skin_ips .cke_button a.cke_on:active {
   box-shadow: 1px 1px 2px rgba(0, 0, 0, .5) inset !important;
}
body.cke_panel_frame .cke_panel_block {
   background: #AAA !important;
}
.cke_panel_listItem.cke_selected a, .cke_panel_listItem a:hover, .cke_panel_listItem a:focus, .cke_panel_listItem a:active,
a.cke_colorbox:hover, a.cke_colorbox:focus, a.cke_colorbox:active, a.cke_colorauto:hover, a.cke_colorauto:focus, a.cke_colorauto:active, a.cke_colormore:hover, a.cke_colormore:focus, a.cke_colormore:active {
   background: #EEE !important;
   border-color: #444 !important;
   color: #333 !important;
   text-decoration: none !important;
   outline: none !important;
}
.ipsSmileyTray {
   background-color: #DDD !important;
   border-color: #AAA !important;
}

.cke_wrapper iframe {
   border: 1px inset #888 !important;
}
body.cke_show_borders {
   background: -moz-linear-gradient(top, rgba(210,210,210,1) 0%, rgba(235,235,235,1) 43%, rgba(255,255,255,1) 100%) no-repeat !important;
   background-attachment: fixed !important;
}

#iframeUploadForm {
   background-color: #F7F7F7 !important;
   padding-bottom: 5px !important;
}

body#ipboard_body fieldset.submit, body#ipboard_body p.submit {
   background-color: transparent !important;
}
.ipsBox_container.ipsPostForm_sidebar {
   z-index: 999 !important;
   width: 243px !important;
}
.bar {
   background-color: #EAEAEA !important;
}

/* 
 * ========================================
 * Members - Global
 * ========================================
 */

.ipsVerticalTabbed_tabs > ul {
   border-color: #000 !important;
}
.ipsVerticalTabbed_tabs li {
   border: none !important;
}
.ipsVerticalTabbed_tabs li a {
   background: -moz-linear-gradient(top, rgba(86,86,86,0.8) 0%, rgba(0,0,0,0.8) 100%) #FFF !important;
   color: #DEDEDE !important;
}
.ipsVerticalTabbed_tabs li a:hover {
   background: -moz-linear-gradient(top, rgba(86,86,86,0.9) 0%, rgba(0,0,0,0.9) 100%) #FFF !important;
   text-decoration: none !important;
}
.ipsVerticalTabbed_tabs li.active a {
   background: -moz-linear-gradient(top, rgba(86,86,86,1) 0%, rgba(0,0,0,1) 100%) #FFF !important;
   width: 133px !important;
   border: none !important;
   text-shadow: 0px 0px 2px #FFFFFF, 0px 2px 4px #FFFFFF, 2px 2px 4px #FFFFFF, 2px 0px 4px #FFFFFF, 2px -2px 4px #FFFFFF, 0px -2px 4px #FFFFFF, -2px -2px 4px #FFFFFF, -2px 0px 4px #FFFFFF !important;
   color: #222 !important;
}
.ipsVerticalTabbed_minitabs.ipsVerticalTabbed_tabs li.active a {
   width: 23px !important;
}

.reputation.positive, .members li.positive {
   background: -moz-linear-gradient(top, rgba(121,153,216,1) 0%, rgba(55,108,214,1) 23%, rgba(30,30,216,1) 100%) !important;
   box-shadow: 1px 1px 0px #10109A !important;
   border: 1px solid #15159F !important;
}
.reputation.zero {
   background: -moz-linear-gradient(top, rgba(230,237,242,1) 0%, rgba(199,204,207,1) 26%, rgba(164,178,184,1) 100%) !important;
   box-shadow: 1px 1px 0px #666 !important;
   border: 1px solid #777 !important;
   color: #222 !important;
}

/* 
 * ========================================
 * Members - Profile
 * ========================================
 */

.ipsUserPhoto {
   box-shadow: 1px 1px 0px #444, 3px 3px 3px rgba(0, 0, 0, 0.8) !important;
   border: 1px solid #444 !important;
   border-radius: 3px !important;
   padding: 0px !important;
}

.progress_bar {
   border-color: #BBB !important;
}
.progress_bar span {
   background: -moz-linear-gradient(top, rgba(121,153,216,1) 0%, rgba(55,108,214,1) 23%, rgba(30,30,216,1) 100%) !important;
}

#profile_panes_wrap .general_box {
   border: 1px solid #222 !important;
}
#profile_panes_wrap .general_box h3 {
   background: -moz-linear-gradient(top, rgba(0,0,0,1) 0%, rgba(52,52,52,1) 21%, rgba(98,98,98,1) 49%, rgba(61,61,61,1) 80%, rgba(0,0,0,1) 100%) !important;
   color: #DEDEDE !important;
