css:
/* ================== server settings ======================== */
@-moz-document domain("bikerornot.com")
/* =========================================================== */
/*   v.1.2e  Update to Remove the new Trending links section   */
/* =========================================================== */
 {     
/* ================= Main body stuff ======================= */
body,
.body,
div[class="hfc navbar-holder"],
div[id="event-all-attend-wrapper"],
div[id="bn-foot2"],
div[id="browse-holder"]{
    background: url("http://res.cloudinary.com/mrquinn/image/upload/v1460652230/styles/OldWood_Grey.jpg") #000000 fixed repeat!important;
    color: #cccccc!important;
}
li[class="me"] {
    color: #07771F!important;
}

/*------ Links ----*/
a:link,
a:visited,
a:active {
    font-family: "Book Antiqua", "Palatino Linotype", "Comic Sans MS"!important;
    color: #ffe081!important;
    padding: 0px 1px 0px 1px!important;
    font-size: 12px!important;
}
a:hover {
    font-size: 12px;
    padding: 0px 1px 0px 1px;
    text-decoration: none;
    border: 1px solid #ffe081;
    background-image: url("http://res.cloudinary.com/mrquinn/image/upload/v1460652230/styles/OldWood_Grey.jpg");
    -moz-border-radius: 5px;
    border-radius: 5px;
    -webkit-border-radius: 5px;
}
/* new blog style cleanup & ugly status box */

div [class="link-media"],
div [class="feed-item"] {
    border: none!important;
}
div [class="status-wrapper"] {
    background-color: transparent!important;
    border: none!important;
}
input[type="submit"] {
    background-color: #000000!important;
}
.comment-btn {
    color: #cccccc !important;
}
textarea {
    color: #000000!important;
}
div [class="filter active"],
div [class="info-holder"] {
    border: 1px solid #ffe081;
    background-image: url("http://res.cloudinary.com/mrquinn/image/upload/v1460652230/styles/OldWood_Grey.jpg");
    -moz-border-radius: 5px;
    border-radius: 5px;
    -webkit-border-radius: 5px;
}
h1,
h2,
h4,
h5,
p {
    color: #cccccc!important;
}
h3 {
    color: #E22F50!important;
}
h1 [class="tbig left"] {
    color: #3B5998!important;
}
b {
    color: #3B5998!important;
}
div [class="icon-bar"] {
    background-color: #000000 !important;
}
.menu {
    background-image: url("http://res.cloudinary.com/mrquinn/image/upload/v1460652230/styles/OldWood_Grey.jpg")!important;
    -moz-border-radius: 5px;
    border-radius: 5px;
    -webkit-border-radius: 5px;
}
/*-- user online/offline color--*/

.b {
    color: #07771F !important;
}
/*---- hide the boring stuff ------ */

div[class="trending-hashtag-table"],
div[id="bn-foot"] {
    display: none!important;
}
/* ======================== Global Settings ================= */

.bx-ttl h2 {
    color: #3B5998!important;
}
.bn-c-prf-c4 {
    color: #cccccc!important;
}
img [class="browse-next"],
img [class="browse-next"] {
    background-color: #000000!important;
}
div [id="bn-3col2w"] {
    background-color: #000000!important;
}
div [id="idAlbum_menu_left"] {
    color: #cccccc !important;
}
/* block the admin ads */

div [class="feed-item feed-ad"] {
    display: none!important;
}
div [class="bx-ttl"],
div [class="likebikestop"],
div [class="commenttopdiv"],
div [class="feed-filters bn-c-msg"] {
    background: url("http://res.cloudinary.com/mrquinn/image/upload/v1460652230/styles/OldWood_Grey.jpg") repeat!important;
}
div [class="paging paging:"] {
    color: #cccccc!important;
}
div [class="paging paging:search"] {
    color: #cccccc!important;
}
td {
    background-color: #000000!important;
    color: #cccccc!important;
}
span [class="ttl"] {
    color: #3B5998 !important;
}
a span {
    color: #3B5998 !important;
    border: none !important;
}
span [class="nxtprv"] {
    color: white !important;
}
a [class="nxtprv"] {
    color: white !important;
}
div [class="feed_attach"] {
    color: #cccccc !important;
}
div [class="subhead"] {
    color: #3B5998
}
img {
    border: none !important;
}
div [id="hdtSearchFixed"] {
    background-color: #ffffff !important;
}
.bn-c-msg {
    background-color: transparent !important;
}
.comm_outer,
.feed_inner,
.feed_comm {
    background: transparent !important;
}
div [class="feed_comm feed_comm_more"] {
    color: #cccccc !important;
    background: transparent !important;
}
/*---------- User Chat Window -----------*/

div[id="tab-section"],
div[id="tab-holder"],
div[id="tabs"],
div[id="tab-slide-left"],
[class="tab tab-slide"] {
    background: url("http://res.cloudinary.com/mrquinn/image/upload/v1460652230/styles/OldWood_Grey.jpg") fixed repeat!important;
    color: #cccccc!important;
}
div[class="tab tab-user user-19260 first active"] {
    background: url("http://res.cloudinary.com/mrquinn/image/upload/v1460652230/styles/OldWood_Grey.jpg") fixed repeat!important;
    color: #cccccc!important;
    border: 1px solid #ffe081 !important;
}
div[id="body-section"] {
    background-image: none !important;
}
div[class="conv"] {
    background-color: #cccccc !important;
}
p[class="waiting"],
input[class="chat-text"] {
    color: #000000 !important
}
/* ================ block those annoying ads ===============================*/

.ad_box {
    display: none !important;
}
div#googMultiAdvert {
    border: none !important;
}
iframe {
    display: none!important;
}
div#bn-3col3n {
    display: none !important;
}
/* ------- Slide out box ad in lower right corner --------- */

a[id="ad_panel"] {
    display: none !important;
}
img[id="ad_image"] {
    display: none !important;
}
span[id="ad_bg"] {
    display: none !important;
}
/* ------- the big google ads... top, right & bottom -------- */

div[id="google_ads_div_bon_top_fold_728_90_ad_container"] {
    display: none!important;
}
div[id="google_ads_div_BON_left_160_600_ad_container"] {
    display: none!important;
}
div[id="google_ads_div_Bottom_Page_ad_container"] {
    display: none!important;
}
div [class="ad ad728x90"] {
    display: none!important;
}
div [id="google_ads_div_BN!RightTopUserProfile"] {
    display: none!important;
}
div[id="bon_topAd"] {
    display: none!important;
}
div [class="ad ad160x600"] {
    display: none!important;
}
/* ===== blocks bikes for sale ads at the top ======
 if you want these comment out or remove this section
 ------- */

div[id="cxAd"] {
    display: none!important;
}
/* ============================================ */
/*------ End ----*/
}
