css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wiki.mcclatchyinteractive.com") {

#bodyContent h1 {
   margin-top: 1em !important;
   padding-top: .25em !important;
   font-weight: bold !important;
   border-top: 3px solid black !important;
   border-bottom: none !important;
}

#bodyContent h2 {
   margin-top: 1em !important;
   font-weight: normal !important;
   border-bottom: 2px solid #666 !important;
}

#bodyContent h3 {
   margin-top: 1em !important;
   font-weight: normal !important;
   font-size: 135% !important;
   border-bottom: 1px solid silver !important;
}

#bodyContent p,
#mw_contentholder p {
    font-size: 14px !important;
    font-family: 'palatino linotype', serif !important;
}

#bodyContent li,
#mw_contentholder li {
   font-family: 'palatino linotype', serif !important;	
   font-size: 14px !important;
}

#bodyContent i,
#bodyContent tt,
#mw_contentholder tt,
#bodyContent code,
#mw_contentholder code {
   color: #823CCE;
}

code,
tt,
pre {
   font-family: monospace !important;
   font-style: normal !important;
   font-weight: normal !important;
}

pre,
pre code {
   font-size: 100% !important;
   padding: .5em .5em !important;
   line-height: 1.2em !important;
   background: #fafafa !important;
   border: 1px solid #ddd !important;
   /* the following styles allow pre text to wrap: */
   overflow-x: auto;                      /* Use horizontal scroller if needed */
   white-space: pre-wrap !important;      /* css-3 */
   white-space: -moz-pre-wrap !important; /* Mozilla, since 1999 */
   white-space: -pre-wrap;                /* Opera 4-6 */
   white-space: -o-pre-wrap;              /* Opera 7 */
   word-wrap: break-word;                 /* Internet Explorer 5.5+ */
}

#toc li {
   font-family: 'lucida grande', sans-serif !important;
   font-size: 11px !important;
   margin-top: 0; margin-bottom: 0;
}

.editsection { display: none; }

}
