css:
/* Eric Meyer's Reset CSS v2.0 - http://cssreset.com */
html,body,div,span,applet,object,iframe,h1,h2,h3,h4,h5,h6,p,blockquote,pre,a,abbr,acronym,address,big,cite,code,del,dfn,em,img,ins,kbd,q,s,samp,small,strike,strong,sub,sup,tt,var,b,u,i,center,dl,dt,dd,ol,ul,li,fieldset,form,label,legend,table,caption,tbody,tfoot,thead,tr,th,td,article,aside,canvas,details,embed,figure,figcaption,footer,header,hgroup,menu,nav,output,ruby,section,summary,time,mark,audio,video{border:0;font-size:100%;font:inherit;vertical-align:baseline;margin:0;padding:0}article,aside,details,figcaption,figure,footer,header,hgroup,menu,nav,section{display:block}body{line-height:1}ol,ul{list-style:none}blockquote,q{quotes:none}blockquote:before,blockquote:after,q:before,q:after{content:none}table{border-collapse:collapse;border-spacing:0}

html > body {
	background-color: #f3f2ee !important;
	color: #000000 !important;
	text-shadow: none !important;
	-webkit-text-fill-color: none !important;
	font: inherit;
}

html > body, html > body *, p, p * {
	color: #000000 !important;
	font-size: 16pt !important;
	line-height: 24pt !important;
	font-family: Georgia !important;
}


html > body, html > body *:not([onclick]):not(:link):not(:visited) {
	background-image: none !important;
}

html > body a:link, html > body a:link *,html > body a:link:hover, html > body a:link:hover *,html > body a:link:active, html > body a:link:active * {
	color: #065588 !important;
	}

html > body a:visited, html > body a:visited *,html > body a:visited:hover, html > body a:visited:hover *,html > body a:visited:active, html > body a:visited:active * {
	color: #065588 !important;
}

table {
    width: 90% !important;
}

/* Evernote Clearly fix */
div[style^="position: absolute; z-index: 99999999; display: block;"] p {
    color: #eee !important;
}
