css:
@-moz-document domain(blockland.us){

/* Normal, standard links. */
a:link, a:visited
{
	color: #ff7700 !important;
}
a:hover
{
	color: #ccc !important;
}

/* Navigation links - for the link tree. */
.nav, .nav:link, .nav:visited
{
	color: #000000 !important;
}
a.nav:hover
{
	color: #f00 !important;
}

/* By default (td, body..) use Verdana in black. */
body, td, th
{
	color: #000 !important;
}

/* Input boxes - just a bit smaller than normal so they align well. */
input, textarea
{
	color: #000 !important;
	background-color: #fff !important;
}

/* Selects are a bit smaller, because it makes them look even better 8). */
select
{
	color: #000 !important;
	background-color: #fff !important;
}

/* Standard horizontal rule.. */
hr
{
	color: #f70 !important;
}
/* A more colorful hr.. */
.hrcolor
{
	color: #f70 !important;
	background-color: #f70 !important;
}

/* A quote, perhaps from another post. */
.quote
{
	color: #000 !important;
	background-color: #eee !important;
}

/* /me uses this a lot. */
.meaction
{
	color: #f00 !important;
}

/* Highlighted text - such as search results. */
.highlight
{
	background-color: #f70 !important;
	color: #fff !important;
}

/* Alternating backgrounds... */
.windowbg
{
	background-color: #ffeada !important;
}

/* Titles - such as table headers. */
.titlebg, tr.titlebg th, tr.titlebg td, .titlebg a:link, .titlebg a:visited, .titlebg2, tr.titlebg2 th, tr.titlebg2 td, .titlebg2 a:link, .titlebg2 a:visited
{
	color: #ffffff !important;
	background-color: #ff822a !important;
	background-image: url(http://dl.dropbox.com/u/27975145/BLF%20themes/AA/maincatbg.png) !important;
}

/* The category headers, page indexes, and such things. */
.catbg
{
	background-color: #ff822a !important;
	background-image: url(http://dl.dropbox.com/u/27975145/BLF%20themes/AA/catbg.png) !important;
	color: #FFF !important;
}

.catbg a:visited, .catbg a:link
{
	color: #fff !important;
}
.catbg a:hover
{
	color: #000 !important;
	text-decoration: none !important;
}
.catbg td
{
	color: #FFF !important;
}
}
