css:
/*
	Name: "JTV Not Suck So Much" v1.1
	By: @CornDoggyRob
	For: http://justin.tv
	
	"Using CSS to make the web suck a little less"
	CHAT IS DISABLED BECAUSE CHAT SUX!
*/

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("justin.tv");
body {
	background:none !important;
	background-color: #2f2f2f !important;
	border: 0px !important;
}

#actions {
	border: 0px !important;
}

#site_header {
	background-color: #2f2f2f !important;
	height: 50px !important;
}

li#header_language_stem {
	display:none !important;
}

#banner_default {
	display:none !important;
}

#banner_custom {
	display:none !important;
}

#admin_nxtchan.admin_nxtchan.below_header {
	display:none !important;
}

#directory.wrapper.discover.clearfix {
	background-color: #dfdfdf !important;
	-webkit-border-radius: 8px !important;
	-moz-border-radius: 8px !important;
	border-radius: 8px !important;
}



.left_col_bg {
	height: 575px !important;
        padding-left:7px !important;
	-webkit-border-top-left-radius: 8px 8px !important;
	-webkit-border-bottom-left-radius: 8px 8px !important;
	-moz-border-radius-topleft: 8px 8px !important;
	-moz-border-radius-topleft: 8px 8px !important;
	border-top-left-radius: 8px !important;
	border-bottom-left-radius: 8px !important;
	background-color: #dfdfdf !important;
}

#related {
	margin-top:0px !important;
}

.right_col_rnd {
	background-color: #dfdfdf !important;
}

/* comment or remove to enable chat*/
#chat_container {
	display:none !important;
}

.right_col_rnd a {
	color: #2f2f2f !important;
}

#related.right_col_rnd ul#related_list.panel {
	/* MOZILLA SUBTRACT 1px */
	height:555px !important;
}

#channel_status_message {
	color: #2f2f2f !important;
}

#about {
	display:none !important;
}

.left_col_bottom {
	display:none !important;
}

li.list_item.channel.horiz-1_col.clearfix {
	border-bottom:none !important;
}
#actions {
	border: none !important;
	border-bottom: none !important;
}

#actions #action_contents.clearfix {
	display:none !important;
}

.dark #action_links {
	border-bottom: 0px !important;
}

.light #action_links {
	border-bottom: 0px !important;
}


span#share_arrow.action_arrow.col_bg {
	display:none !important;
}

#footer {
	display:none !important;
}

img {
	-webkit-border-radius: 3px !important;
	-moz-border-radius: 3px !important;
	border-radius: 3px !important;
}

a.hdr_lg.broadcast_btn {
	background-color: #2f2f2f !important;
}

/* DIRECTORY BROWSING */
#directory.wrapper.discover.clearfix div.left_col {
width: 99% !important;
}

#socialcam_banner_promo {
	display:none !important;
}

#meebo-canvas-6 {
	display:none !important;
}
