css:
@-moz-document url-prefix('http://www.uc-forum.com/forum/')
{
	*
	{
		background-color: #111 !important;
		background-image: none !important;
		border-color: #888 !important;
		color: #CCC !important;
	}

	/* Border Fix */
	table.tborder,
	#collapseobj_cyb_fh_stats > tr > td
	{ 
		border: 1px solid #888 !important; 
	}

	#collapseobj_cyb_fh_stats td.alt1[width="1%"],
	#collapseobj_cyb_fh_stats td.alt2[width="1%"]
	{ 
		border: 1px solid transparent !important;
	}

	/* code, quote, php box fix */
	div.alt2[dir=ltr],
	pre.alt2[dir=ltr] 
	{
		width: 920px !important;
		border: 1px solid #888 !important;
	}

	/* top menu fix */
	body > div > table > tbody > tr > td > table:nth-child(2) *
	{ 
		background-color: #F7F7F7 !important; 
		background-image: url('http://www.uc-forum.com/forum/ambience/misc/ambience_nav_x.gif') !important;
	}

	/* editor fix */
	.vBulletin_editor *
	{
		background-color: #FFF !important;
		border-color: #000 !important;
		color: #000 !important;
	}

	/* shoutbox fix */
	#dbtech_shoutbox_content1 .alt1,
	#dbtech_shoutbox_content1 .alt1 *
	{ 
		background-color: #101010 !important;
		border-color: #101010 !important;
	}

	#dbtech_shoutbox_content1 .alt2,
	#dbtech_shoutbox_content1 .alt2 *
	{ 
		background-color: #151515 !important; 
		border-color: #151515 !important; 
	}

	/* name color fix */
	[color="red"],		[style="color:red"],		[color="red"]		* { color: red 			!important; }
	[color="darkred"],	[style="color:darkred"],	[color="darkred"]	* { color: darkred 		!important; }
	[color="orange"],	[style="color:orange"],		[color="orange"] 	* { color: orange 		!important; }
	[color="navy"],		[style="color:navy"],		[color="navy"]		* { color: royalblue	!important; }
	[color="blue"],		[style="color:blue"],		[color="blue"]		* { color: deepskyblue	!important; }
	[color="#EC1313"],	[style="color:#EC1313"],	[color="#EC1313"] 	* { color: #EC1313 		!important; }
	[color="#348781"],	[style="color:#348781"],	[color="#348781"] 	* { color: #348781 		!important; }
	[color="#2d7009"],	[style="color:#2d7009"],	[color="#2d7009"] 	* { color: #2d7009 		!important; }
	[color="#af00af"],	[style="color:#af00af"],	[color="#af00af"] 	* { color: #af00af 		!important; }
	[color="#00CC00"],	[style="color:#00CC00"],	[color="#00CC00"] 	* { color: #00CC00 		!important; }
	[color="#8b814c"],	[style="color:#8b814c"],	[color="#8b814c"] 	* { color: #8b814c 		!important; }
	[color="#2F4F4F"],	[style="color:#2F4F4F"],	[color="#2F4F4F"] 	* { color: #437070 		!important; }

	/* php code fix */
	[style="color: #FF8000"]	{ color: #FF8000	!important; }
	[style="color: #0000BB"] 	{ color: #70B8FF 	!important; }
	[style="color: #007700"] 	{ color: #B8FF70 	!important; }
	[style="color: #DD0000"] 	{ color: #FF3333 	!important; }

	/* images removed */
	img[src="http://www.uc-forum.com/forum/ambience/misc/ls_main_table_top.gif"],
	img[src="http://www.uc-forum.com/forum/ambience/misc/rs_main_table_top.gif"],
	img[src="http://www.uc-forum.com/forum/ambience/misc/expand_main_table.gif"],
	img[src="http://www.uc-forum.com/forum/ambience/misc/cat_top_rs.gif"],
	img[src="http://www.uc-forum.com/forum/ambience/misc/cat_top_ls.gif"],
	img[src="http://www.uc-forum.com/forum/ambience/misc/rs_main_table_bottom.gif"],
	img[src="http://www.uc-forum.com/forum/ambience/misc/ls_main_table_bottom.gif"],
	img[src="ambience/misc/ls_main_table_top.gif"],
	img[src="ambience/misc/rs_main_table_top.gif"],
	img[src="ambience/misc/expand_main_table.gif"],
	img[src="ambience/misc/cat_top_rs.gif"],
	img[src="ambience/misc/cat_top_ls.gif"],
	img[src="ambience/misc/rs_main_table_bottom.gif"],
	img[src="ambience/misc/ls_main_table_bottom.gif"]
	{
		display: none;
	}
}
