css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(caakee.com) {

* {
font-family: Verdana, Tahoma, Helvetica,Arial, 微软雅黑, 宋体, sans-serif !important;
}
a:hover {
text-decoration: none;
}
div.header {
height: 63px;
}
div.header div.logo {
top:0px;
height:40px;
}
div.header div.product_version {
top:0px;
}
div.header div.product_name {
top:16px;
}
div.header div.sys_tool_bar {
margin-left: 241px;
margin-right: 0px;
width: 674px;
}
.weather {
display:none;
}
div.header div.nav ul.items li {
-webkit-border-top-left-radius: 5px;
-webkit-border-top-right-radius: 5px;
-moz-border-radius-topleft: 5px;
-moz-border-radius-topright: 5px;
border-top-left-radius: 5px;
border-top-right-radius: 5px;
border-color:#58b8ea;
}

div.box div.head ul li {
font-size:16px;
}
div.header div.nav ul.items li:hover {
background:#349FD7;
}

div.header div.nav ul.items li:hover a {
font-weight: bold;
color: white;
}

div.box div.account ul li {
font-size:20px;
line-height:1.3em;
height:27px !important;
}

div.box div.account ul li.active {
background-color:#ffe1a0;
}

div.box div.account ul li.balance {
height:25px;
}

div.box div.body ul li a {
display: inline-block;
width: 153px;
}

div.box div.body ul li {
padding-left: 10px;
font-size:15px;
line-height:1.3em;
height:23px !important;
}

div.box div.body ul#category_board li a {
width: 142px;
}

div.box div.account ul li:hover a {
color: #F27B04;
font-weight: bold;
}

#listtb {
font-size:13px;
}

#tally_board div.head ul li {
-webkit-border-top-left-radius: 5px;
-webkit-border-top-right-radius: 5px;
-moz-border-radius-topleft: 5px;
-moz-border-radius-topright: 5px;
border-top-left-radius: 5px;
border-top-right-radius: 5px;
border-color:#58b8ea;
}

table.listtable td.number {
font-weight: bold;
}

.body #entry table.listtable td:nth-child(6) {
color:#207200;
}

.body #entry table.listtable td:nth-child(7) {
color:#990000;
}

}
