/*
 * Author:            Will (war59312@gmail.com)
 * Description:       Article only pages for infoworld.com. Removes all the junk from articles. A lot easier to read.
*/

/* +++ changelog +++
September 25, 2011
- original release
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("infoworld.com") {

/* Headers */
#sidekick_container { display: none !important; }
#block-infoworld-infoworld_header_domains { display: none !important; }
#block-infoworld_nav-navigation_domain { display: none !important; }
#block-infoworld-infoworld_header { display: none !important; }
.header { display: none !important; }


/* Newsletter */
#block-infoworld-newsletter_subscribe, #newsletter { display: none !important; }

/* Most Popular Articles */
#block-infoworld-most_popular_articles { display: none !important; }

/* Recommended Resources */
div[id*="recommended_resources"] { display: none !important; }

/* IT Jobs */
#it_job_module { display: none !important; }

/* Recent Posts  */
#block-infoworld-blog_recent_posts { display: none !important; }

/* Most Popular  */
#block-block-39, #mostPopular { display: none !important; }

/* Comments */
.disqus-comments.comments.left, #block-idge_disqus-idge_disqus_comments { display: none !important; }

/* Sign Up For Newsletter Link Ad */
.blog_nl_signup_link, .article_nl_signup_link { display: none !important; }

/* Promo Ad After Article */
#block-block-72, .promo { display: none !important; }

/* Additional Resources */
#block-infoworld-additional_resources { display: none !important; }

/* Sponsored Links */
#block-infoworld-sponsored_links, #block-block-71, .featuredSponsor-strip, div[class*="Sponsor"] { display: none !important; }

/* Marketplace */
#block-infoworld-technology_marketplace { display: none !important; }

/* Footer */
#block-block-21, #footer, #block-block-20, #footerBottom { display: none !important; }

}
