css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("skybyen.no") {

/* Forside */

#cFrontpageWrapper .mod_gray_header {
height: 25px !important;
}

.ansikter_man, .ansikter_woman, .ansikter_age, .mod_gray_body a {
color: #653F97 !important; 
text-shadow: none !important;
}

body, div, dl, dt, dd, ul, ol, li, h1, h2, h3, h4, h5, h6, pre, form, fieldset, textarea, p, blockquote, th, td, .fbConnectWidgetTopmost {
background: none transparent !important;
border: none !important;
color: #653F97 !important;
text-shadow: none !important;
}

/* Bilder */

.pdvImg span {
background-image: none !important;
}

.pdvImg img {
border: none !important;
border-radius: 20px !important;
-moz-box-shadow: 0px 0px 5px 2px #653F97;
-icab-box-shadow: 0px 0px 5px 2px #653F97;
-khtml-box-shadow: 0px 0px 5px 2px #653F97;
-webkit-box-shadow: 0px 0px 5px 2px #653F97;
}

.btnBlue110x29 {
border: none !important;
border-radius: 5px !important;
-moz-box-shadow: 0px 0px 5px 2px #653F97 !important;
-icab-box-shadow: 0px 0px 5px 2px #653F97 !important;
-khtml-box-shadow: 0px 0px 5px 2px #653F97 !important;
-webkit-box-shadow: 0px 0px 5px 2px #653F97 !important;
background: none repeat scroll 0 0 #090909 !important;  
}

/* Ask Me */

.askmeQRowAnswerSend input, .askAQSend input, .pagination span, .pagination a:hover {
border: none !important;
border-radius: 5px !important;
-moz-box-shadow: 0px 0px 5px 2px #653F97 !important;
-icab-box-shadow: 0px 0px 5px 2px #653F97 !important;
-khtml-box-shadow: 0px 0px 5px 2px #653F97 !important;
-webkit-box-shadow: 0px 0px 5px 2px #653F97 !important;
background: none repeat scroll 0 0 #090909 !important; 
}

.pagination a, .pagination span {
text-shadow: none !important;
color: #653F97 !important;
}

#community-wrap .cSubmenu {
margin-top: 5px !important;
}

#community-wrap .cSubmenu a {
background: none repeat scroll 0 0 #090909 !important; 
text-shadow: none !important;
border-radius: 15px !important;
}

#community-wrap .cSubmenu a:hover {
color: #fff !important;
}

#community-wrap ul.submenu li .active:link, #community-wrap ul.submenu li .active:visited, #community-wrap ul.submenu li .active:hover {
background: none repeat scroll 0 0 #090909 !important; 
z-index: 10 !important;
border: none !important;
border-radius: 15px !important;
}

/* Forum */

#Kunena td.kprofileboxcnt ul.kprofilebox-welcome li input.kbutton, #Kunena .klist-markallcatsread input.kbutton {
background: none repeat scroll 0 0 #090909 !important; 
text-shadow: none !important;
color: #653F97 !important;
border-radius: 5px !important;
}

#Kunena td.kprofileboxcnt ul.kprofilebox-welcome li input.kbutton:hover, #Kunena form#jumpto input.kbutton:hover, #Kunena .klist-markallcatsread input.kbutton:hover {
border-radius: 5px !important;
color: #fff !important;
-moz-box-shadow: 0px 0px 5px 2px #653F97 !important;
-icab-box-shadow: 0px 0px 5px 2px #653F97 !important;
-khtml-box-shadow: 0px 0px 5px 2px #653F97 !important;
-webkit-box-shadow: 0px 0px 5px 2px #653F97 !important;
background: none repeat scroll 0 0 #090909 !important; 
}

/* Hot or Not */

img.hotornot-icon {
box-shadow: none !important;
}

.vote-zone img {
border: none !important;
border-radius: 15px !important;
-moz-box-shadow: 0px 0px 5px 2px #653F97;
-icab-box-shadow: 0px 0px 5px 2px #653F97;
-khtml-box-shadow: 0px 0px 5px 2px #653F97;
-webkit-box-shadow: 0px 0px 5px 2px #653F97;
}

.faux_button {
background: none repeat scroll 0 0 #090909 !important;
border-radius: 5px !important;
}

.faux_button:hover {
background: none repeat scroll 0 0 #090909 !important;
border-radius: 5px !important;
color: #fff !important;
-moz-box-shadow: 0px 0px 5px 2px #653F97;
-icab-box-shadow: 0px 0px 5px 2px #653F97;
-khtml-box-shadow: 0px 0px 5px 2px #653F97;
-webkit-box-shadow: 0px 0px 5px 2px #653F97;
}

/* SÃ¸k */

.pagination strong, .pagination a {
border: none !important;
background: none repeat scroll 0 0 #090909 !important;
}

.com_usersearch label {
text-shadow: none !important;
color: #653F97 !important;
}

.com_usersearch #city, .com_usersearch #username, .com_usersearch #age_to, .com_usersearch #age_from, .com_usersearch #gender, .com_usersearch #status, .com_usersearch #seeking, .com_usersearch #orientation, .com_usersearch #county, .com_usersearch #online {
background: none repeat scroll 0 0 #090909 !important;
border-radius: 5px !important;
}

input[type="submit"], button, .faux_button, .rsform-submit-button, .rsform-button {
background: none repeat scroll 0 0 #090909 !important;
border-radius: 5px !important;
}

.rsform-button:hover {
background: none repeat scroll 0 0 #090909 !important;
border-radius: 5px !important;
color: #fff !important;
-moz-box-shadow: 0px 0px 5px 2px #653F97;
-icab-box-shadow: 0px 0px 5px 2px #653F97;
-khtml-box-shadow: 0px 0px 5px 2px #653F97;
-webkit-box-shadow: 0px 0px 5px 2px #653F97;
}

/* Profil */

body {
background-image: url("http://www.wallchan.com/images/sandbox/1305828451-floor-minimalistic-dark-pattern-wood-patterns-wallpaper.jpg") !important;
background-attachment: fixed !important;
background-position: top center !important;
background-color: #000 !important;
}

.headerBlackbg, .logo_and_ad, .spotlight_img_overlay {
background: none !important;
}

.menuBgWrap, .wrapper, .user_dashboard, .wrapper_mainTop, .dashItem { 
background: none transparent !important; 
color: #653F97 !important;
}

.user_info_module, body #community-wrap div.profile-right, .profile-right .mod_gray_header { 
border-right: 0px solid #090909 !important; 
}

.fatka_info, .spotlight_userinfo, .shoutBoxMsg, .shoutbox_message, .dashItem a {
color: #653F97 !important; 
}

.rightAdvert, .askmeRight, .visitor_module_item { 
background: none #090909 !important; 
border: 1px solid #090909 !important; 
border-radius: 10px !important; 
box-shadow: 0 0 5px black inset !important;
color: #653F97 !important;
}

#menu a.level1 span.bg, #menu span.level1 span.bg { 
border-left: 0px solid #090909 !important; 
border-right: 0px solid #090909 !important; 
color: #653F97 !important; 
text-shadow: none !important; 
}

.dashItem a, .duumLiOpener, .amrQuestion a {
text-shadow: none !important; 
color: #653F97 !important;
}

.profile-right .mod_gray_header, .user_info_module { 
border: none !important; 
}

.wrapper, .sky_footer {
width: 1020px !important;
-webkit-border-radius: 0px;
-moz-border-radius: 0px;
border-radius: 0px;
background-color: transparent !important;
background-image:url('http://img84.imageshack.us/img84/2616/skybyenmiddle.png') !important;
border: 1px solid #090909 !important; 
}

.wrapper {
margin-top: 0px !important;
}

.mod_gray_header h3 { text-align: center !important; padding: 3px !important; color: #653F97 !important; text-shadow: 3px 3px 5px #000, 0 0 4px #fff, 0 -2px 2px #fff, 2px -4px 3px #000, -2px -3px 4px #000, 2px -3px 3px #999 !important; }

.dashSearch #ajxusrsearch {
background: none repeat scroll 0 0 #090909 !important; 
border-radius: 10px; box-shadow: 0 0 5px black inset !important; 
width: 195px !important;
color: #653F97 !important;
}

body #community-wrap div.inbox-read, body #community-wrap .diNotification , .mod_gray_header  { 
background: none repeat scroll 0 0 #090909 !important;  
border-radius: 10px !important; 
box-shadow: 0 0 5px black inset !important; 
border: none !important; 
text-shadow: none !important; 
}

.dashItem a, .spotlight_img, .poiAvatar img, .dufAvatar img, .shoutbox_img img, .diNotification {
border-radius: 10px !important;
border: none !important;
-moz-box-shadow: 0px 0px 5px 2px #653F97;
-icab-box-shadow: 0px 0px 5px 2px #653F97;
-khtml-box-shadow: 0px 0px 5px 2px #653F97;
-webkit-box-shadow: 0px 0px 5px 2px #653F97;
background-image:url('http://img84.imageshack.us/img84/2616/skybyenmiddle.png') !important;
text-shadow: 3px 3px 5px #000, 0 0 4px #fff, 0 -2px 2px #fff, 2px -4px 3px #000, -2px -3px 4px #000, 2px -3px 3px #999 !important;
}

.dashItem a:hover {
color: #fff !important;
}

.flirtshare_username_woman, .spotlight_username_woman, .shoutbox_message div a, .vm_infoitem a { 
color: #653F97 !important; 
}

.wrapperBottom { 
background: none transparent !important; 
color: #653F97 !important;
} 

.dufAvatar img, .amrAvatar a img { 
border: 1px solid #090909 !important; 
}

.profile_actions { 
background: none #090909 !important; 
text-shadow: none !important; 
color: #653F97 !important;
text-shadow: 3px 3px 5px #000, 0 0 4px #fff, 0 -2px 2px #fff, 2px -4px 3px #000, -2px -3px 4px #000, 2px -3px 3px #999 !important;
}

.profile_actions_left { 
background: none #090909 !important; 
text-shadow: none !important; 
border-bottom-left-radius: 10px !important; 
border-top-left-radius: 10px !important; 
height: 28px !important; 
color: #653F97 !important;
text-shadow: 3px 3px 5px #000, 0 0 4px #fff, 0 -2px 2px #fff, 2px -4px 3px #000, -2px -3px 4px #000, 2px -3px 3px #999 !important;
}

.profile_actions_right { 
background: none #090909 !important; 
text-shadow: none !important; 
border-bottom-right-radius: 10px !important; 
border-top-right-radius: 10px !important; 
height: 28px !important; 
color: #653F97 !important;
text-shadow: 3px 3px 5px #000, 0 0 4px #fff, 0 -2px 2px #fff, 2px -4px 3px #000, -2px -3px 4px #000, 2px -3px 3px #999 !important;
}

.profile_actions:hover, .profile_actions_left:hover, .profile_actions_right:hover { 
background:none #090909 !important; 
color: #fff !important;
}

#menu .first span { background: none no-repeat scroll center 0 transparent !important; }

/* Inboks */

body #community-wrap .inbox-read img.avatar, body #community-wrap .inbox-unread img.avatar {
border: none !important;
border-radius: 10px !important;
-moz-box-shadow: 0px 0px 5px 2px #653F97;
-icab-box-shadow: 0px 0px 5px 2px #653F97;
-khtml-box-shadow: 0px 0px 5px 2px #653F97;
-webkit-box-shadow: 0px 0px 5px 2px #653F97;
padding: 0px !important;
}

.inbox-reply-form button:hover {
border: none !important;
border-radius: 10px !important;
-moz-box-shadow: 0px 0px 5px 2px #653F97;
-icab-box-shadow: 0px 0px 5px 2px #653F97;
-khtml-box-shadow: 0px 0px 5px 2px #653F97;
-webkit-box-shadow: 0px 0px 5px 2px #653F97;
background: none repeat scroll 0 0 #090909 !important;
}

/* Diverse */

#cometchat_optionsbutton, #cometchat_userstab {
border: none !important;
text-shadow: none !important;
color: #653F97 !important;
}

#cWindowContentWrap, #cWindowContentTop {
background: none repeat scroll 0 0 #090909 !important;
}

.sky_footer a {
color: #653F97 !important;
}

.sky_footer {
display: none !important;
}

.getPlus {
display: none !important;
}

.wrapper_main {
background-image:url('http://img84.imageshack.us/img84/2616/skybyenmiddle.png') !important;
width: 1000px !important;
-webkit-border-radius: 0px;
-moz-border-radius: 0px;
border-radius: 0px;
}

.user_dashboard {
background-image:url('http://img84.imageshack.us/img84/2616/skybyenmiddle.png') !important;
width: 1022px !important;
-webkit-border-radius: 0px;
-moz-border-radius: 0px;
border-radius: 0px;
}

#menu {
background-image:url('http://img84.imageshack.us/img84/2616/skybyenmiddle.png') !important;
width: 1006px !important;
-webkit-border-radius: 0px;
-moz-border-radius: 0px;
border-radius: 0px;
}

}
