css:
/* 
En alternativ syn på Swedroids forum!
*/
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.swedroid.se") {

body
{
	background:#e4e5e0 !important;
}

td
{
	height:0px !important;
}

.page
{
	background:#f6f5ea !important;
	border-width:0 1px !important;
	border-color:#8d8c82 !important;
	border-style:solid !important;
}

.alt2
{
	background:#f0efda !important;
}

#f2, .alt2Active, #f3, .alt1Active
{
	background:#f0efda !important;
}

.alt1
{
	background:#fff !important;
}

.thead, .tfoot
{
	background:#8f9379 !important;
}

select
{
	background: none repeat scroll 0 0 #f0efda !important;
	color:#8d8c82 !important;
}

.tcat, .tcat a
{
	background:#5a5a54 !important;
	color: #ffffff !important;
	text-shadow: 1px 1px 0 #5a5a54 !important;
font-weight: normal !important;
}

.vbmenu_control, .vbmenu_control a
{
	background:#5a5a54 !important;
color: #ffffff !important;
	text-shadow: 1px 1px 0 #5a5a54 !important;
font-weight: normal !important;
}

.fb_share_button
{
	border:0 !important;
visibility: hidden !important;

}



}
