css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.wykop.pl") {
.fix {
position:absolute !important;
}

html {
margin-top:0 !important;
}

#header-con {
background:#4B6A7C !important;
border-bottom:0 !important;
margin-top:0 !important;
-moz-box-shadow:0 0 0 0 rgba(0,0,0,0) !important;
}

a.selected {
background:#fff2cc;
}

.tunder,a:hover,a.tdnone:hover .hvline,.link,.link:visited {
text-decoration:none;
}

.bgfff {
background:#fff !important;
}

.hlight header {
background:#DDEBF2;
padding-top:3px !important;
}

a {
font-family:"Arial",Arial,sans-serif !important;
font-size:13px;
font-weight:normal !important;
text-decoration:none !important;
}

#body-container .wrapper ul.comments-list >li.subcomment cite a {
border:1px solid #E2ECEF;
height:40px;
margin-left:0;
width:40px;
}

.streammini,.marginbott5,.entrymini,#footer-con,#upc {
display:none !important;
}

.margintop30 {
margin-top:10px !important;
}

.wrapper {
padding:0 100px !important;
}

.icon.website {
background-position:-174px 0 !important;
}

section.fleft div.scale {
margin-right:0 !important;
}

nav.main a.selected {
background-color:#4B6A7C !important;
border:0 solid #FF0000 !important;
border-bottom:none;
color:#ffffff !important;
font-weight:bold !important;
padding:5px 10px;
}

.color-4,a.color-4,a.gray.color-4:hover,a.gray.color-4:hover span,.color-1001,a.color-1001,a.gray.color-1001:hover,a.gray.color-1001:hover span,.link.gray,.link.gray>*,.link.gray2,.link.gray2>*,a.link.gray2.hn:hover>*,a.tdnone:hover .link.gray2.hn>*,.ca2,.adotopraca ul li div p span {
color:#FF5917 !important;
font-size:11px !important;
}

.fbold {
font-size:16px !important;
}

.link gray,a.gray.color-3 {
color:#cc321c !important;
}
}
