css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org"),
domain("userscripts.org")

{

/*text&background*/

html, body, #edit-style-options td, .listing-no-screenshot, #main-header, #left-sidebar > *, #imageContainer, #imageDataContainer, #caption, #numberDisplay, #Head, .HeadingTabs, .MessageForm, .Tabs, .Headings, .Box, .PanelInfo li, .Active .TabLink

{
color: white !important;
background: black !important;
}

select, button, form, .TabLink, .TabLink:link, .TabLink:hover, .TabLink:active, .TabLink:visited, a.BigButton, .Button, li.Comment, a.Category, div.MenuTitle, .Attachment:hover .FileHover, .FileMeta div, #MessageForm, #section, .inv, #right, .overview

{
background: black !important;
}

select, input, span, code, textarea, pre, label, div, dd

{
color: white !important;
}

/*links*/

a
{
color: #0040ff !important;
}

a:visited
{
color: #224375 !important;
}

/*highlight*/

textarea, pre, input, code, #view-code, .has-no-options, .CommentForm textarea.TextBox, .Mine, .UserQuote, .Foot, .form-controls tbody tr td input, .form-controls tbody tr td textarea

{
background:#333333!important;
}

}
