css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("thenook.org")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer, fieldset

{
color: white !important;
background: black !important;
}

select, button, fieldset.submit, div#footer_utilities, ul.post_controls li a, #main_search_form #other_filters, #main_search_form .extra_filter fieldset, #main_search_form #main_search, #p-cactions li a, div#member_list ol, div#member_list div

{
background: black !important;
}

div, span

{
background-color: black !important;
}

legend, select, input, span, code, textarea, pre, label, div, dd

{
color: white !important;
}

.post_block, table.ipb_table tr td, li.general_box, table.ipb_table, div.general_box

{
border: 1px solid black !important;
}


/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre, input, code, ul.topic_buttons li a, div#primary_nav ul li.active, div#primary_nav ul li.active a, div#primary_nav ul li:hover, #user_navigation ul#user_link_menucontent a:hover

{
background: #333333 !important;
}

}
