css:
@-moz-document domain('bolsademulher.com'), url-prefix('http://www.bolsademulher.com/') {


#interna_conteudo .materia .canal-pai ,
.canal-pai a {
font-family: 'Josefin Sans', Helvetica Neue Light, Helvetica, Arial, sans-serif;
font-style: normal;
font-size: 30px;
font-weight: 400;
color: #F14E5F;
}

#interna_conteudo #cc_selo {
position: relative;
right: 210px;
top: 12px;
}

#interna_conteudo .materia #coringa h2 {
font-family: Helvetica Neue, Helvetica, Arial, sans-serif;
color: #F14E5F;
margin-top: -10px;
}

#interna_conteudo .materia .canal-pai {
border-bottom:0;
}

#interna_conteudo .materia h1 a {
font-family: Helvetica Neue, Helvetica, Arial, sans-serif;
font-size: 35px;
font-weight: 400;
color: #484848;
}

#interna_conteudo .materia .conteudo u,
#interna_conteudo .materia .conteudo a {
color: #F14E5F !important;
text-decoration:none;
}


#interna_conteudo .materia .conteudo a:hover {
text-decoration:underline;
}

#interna_conteudo .materia #box-control-font {
margin-top: -22px;
}


#interna_conteudo #coringa {
margin-top:-20px;
}

#interna_conteudo .materia #coringa .autor {
position: relative;
top: 40px;
left: 270px;
}

#interna_conteudo .materia .conteudo p {
font-family: Helvetica Neue, Helvetica, Arial, sans-serif;
color: #484848;
line-height: 24px;
font-size: 14px;
}

#interna_conteudo #box_blog,
#interna_conteudo #horoscopo,
#interna_conteudo #home_cloud,
#interna_conteudo .materia .lk-follow-us-twitter,
#interna_conteudo #mobile,
#interna_conteudo .materia #comentarios #coluna_comentarios {
display: none !important;
}

#interna_conteudo .materia .conteudo a {
color: #5F5F5F;
font-family: Helvetica Neue, Helvetica, Arial, sans-serif;
}


#interna_conteudo .materia #box_lateral h3 {
margin-bottom: 13px;
font-family: 'Josefin Sans', Helvetica Neue Light, Helvetica, Arial, sans-serif;
font-style: normal;
font-size: 20px;
font-weight: 600;
color: #F14E5F;
}

#interna_conteudo .materia .veja_mais #tabs,
.materia .veja_mais #tabs .tab.active ,
.materia .veja_mais #container .conteudo.active ,
.materia #box_lateral {
border: 0;
background:none;
}

#interna_conteudo .materia .veja_mais #tabs {
font-family: 'Josefin Sans', Helvetica Neue Light, Helvetica, Arial, sans-serif;
font-style: normal;
font-size: 12px;
font-weight: 600;
color: #F14E5F;
text-transform:uppercase;
}


#interna_conteudo .materia .veja_mais #tabs .tab a {
color: #5f5f5f;
padding: 2px 13.5px 0 0;
}

#interna_conteudo .materia .veja_mais #tabs .tab.active a {
color: #F14E5F;
}

#interna_conteudo .materia .veja_mais #tabs  {
border-bottom: solid 2px #EEEEEF;
}

#interna_conteudo #container .canal {
font-family: 'Josefin Sans', Helvetica Neue Light, Helvetica, Arial, sans-serif;
font-style: normal;
font-size: 12px;
font-weight: 800;
color: #F14E5F !important;
text-transform: uppercase;
}


#interna_conteudo #container h4 {
font-size:16px;
}


#interna_conteudo #box_lateral #container .conteudo p{
line-height:16px;
}


}
