body, .toolbarSearchContainer-withad, .toolbarSearchContainer-withad #toolbarSearch, #insideNYTimesBrowser .summary, #mainTabs li, .articleBody p, .toolsList li a, .toolsList li a:visited, .toolsList li a:hover, h2 a, #insideNYTimesScrollWrapper #insideNYTimesBrowser td, .doubleRule, .doubleRuleDivider, h4, 
