css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com"),
domain("twimg.com"),
domain("akamaihd.net"),
domain("twitpic.com"),
domain("t.co"),
domain("twitgoo.com"),
domain("yfrog.com")

{

/*text&background*/

html, body, legend, p, label, select, .stream-tabs, .tweet-row, .tweet-box h2, .subpage #content h2, .subpage #content .wrapper, .details-pane-tweet, .tweet, #doc, .search-title h2, #page-container,  .user-sidebar-color, .expando-container .dashboard, .page-header, .search-title, .search-header, .main-content, .profile-info .full-name h2, .clearfix:after, .profile-info, .profile-actions, .profile-tweet-box .tweet-box, .preceeding-profile-tweet-box .component, .profile-header, .details-pane .facets-inner .component, .details-pane .user-media-not-displayed-inner, .details-pane .user-media-not-displayed h2, .details-pane .facets-inner h2, .mentioned-in-tweet h3, .related-tweets h3, h3.retweets_count, h3.profile-title, .inner-pane .component, div.pane-toolbar, user-static-list-item .user-screen-name, .user-small-list-item .user-screen-name, .user-profile-link, .stream-end, .status, .timeline-friend, .timeline-header-friend, .friend-actions, .timeline-user-friend, .pane-components, .stream, .signup-field, .signup-body, .signup-head, .friend-actions-btn, .btn, .search, .user-full-name, .bio, .copyright, .user-stats-stat, #content, #upload_form h1, .upload_section, .upload_section h2, .form-hint, #upload_events_wrap, .upload_section_num, #nav-user, #nav-links h1, .upload_sidebar_section p, #upload_sidebar h1, .upload_sidebar_section h2, .upload_options, .uploadifyQueueItem, #photo-comments-form, .trend-location, .you-both-follow, .wrapper, .actions, .notification-bar, .not-displayed-text, .twitter-media-thumbs, .permalink, .clearfix, .twttr-dialog-inside, .profactions, .drop-down, .signup-call-out, .media-not-displayed, .side-section, .header, .tweet-box, .details-pane, .message-inside span, .message-inside, .dismiss, #mobile-gallery-logged-out-header, .footer, div.notice, div.notice p, #user-profile-wrap, #user-profile-bio, #user-profile-left, .sidebar_box, #nav-auth, #photo-comments-login, #nav-unauth, div.profile-follow-button, div.user-profile-actions-button, div.js-direct-message-action, a.profile-unfollow-link, .expanding-stream-item, div.component, div.full-name h2, .profile-subpage-title h1, .profile-subpage-title h2, .user-content-medium .user-content-rest .full-name, div.user-description, .stream-user-buttons .button, div#top-bar-bg, div.upper-nav, .replies

{
background: black !important;
color: white !important;
}

h5, button, input, strong, textarea, dt, .tweet-full-name, #search-query, .twitter-anywhere-tweet-box-editor, .proxy-editor, em.current, .onebox-title, .onebox-user-name, .onebox-user-screen-name, .stream-tab-title-inner, .tweet-text, div.media-gallery-title, #replyCount, span.fn, .js-stream-title, .fullname, .warning, .banner-outer

{
color: white !important;
}

button, .button, .message-thread-preview,  #front-container, .homepage-gradient, .homepage-gold-bar, div.onebox-inset, .media-slideshow-tweet, .media-gallery-content, #footer, div#menu-search-wrapper, .boxed-area ul a, div#header div, td#content-main, div.aside, div#repliesAndRetweets, td#content-sec, div#header, div.topbar-inner, div.topbar-bg, div.media-gallery-overlay, div.header-inner, div.flex-module, ul.stats, div.new-tweets-bar, div.account, div.component, a.list-link, div.stream-end-inner, div.mini-profile-footer , a.list-action, .front-signin, .front-signup, .global-nav, .global-nav .well, .nav .active > a, .btn-tweet, .btn-tweet:hover, .btn-tweet:active, .tweet .actions, .dropdown-menu, .banner-outer, .permalink-inner, .count-o, .content-inner, .thumbnail-container, #page, #header-outer, .media-thumbnail, .decoded

{
background: black !important;
}

/*links*/

a, .dashboard-component-title, ._old-timestamp, .twitter-atreply, .twitter-timeline-link, .pretty-link, ._timestamp, .favorite-action, .retweet-action, .reply-action, .delete-action, .url, .view-all, .user-follow-link, #account_tab, #password_tab, #devices_tab, #notifications_tab, #profile_tab, #design_tab, #connections_tab, .upper-nav, p.tab-text, div.media-gallery-back-nav span

{
color:#0040ff!important;
}

a:visited

{
color: #224375 !important;
}

/*highlighting*/

.tweet-box .twitter-anywhere-tweet-box-editor, .proxy-editor, .timeline-header-friend a.timeline-header-selected, .upload_section textarea, #create textarea, textarea, input, .error-text, .tab-text:hover, ul.stream-tabs li.active a, ul.tabs li a:hover, ul.tabs li.active a

{
background: #333333 !important;
}

/*lockedprofiles*/

.protected-box
{
background: url("https://twimg0-a.akamaihd.net/a/1318350943/phoenix/img/profile_lock.png") no-repeat scroll right bottom !important;
background-color: #222 !important;
color: white !important
}

}
