background: url('data:image/png;base64,') center no-repeat !important; }

#updates-list .download-progress .cancel,
#list-view  .download-progress .progress .cancel 
{ list-style-image: none !important;
background: url('data:image/png;base64,') center no-repeat !important; }

#updates-list .download-progress .progress .progress-bar,
#list-view  .download-progress .progress .progress-bar 
