.addon-control dropmarker
{ height: 16px !important;
width: 16px !important; }

/* sorter dropmarker up */
.sorter[checkState="2"],
.addon[show-relnotes] .relnotes-toggle .button-icon
{ list-style-image: url(data:image/png;base64,) !important; }

