css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.sumainobaiten.com") {

/* Non-display part */
#twitterFollowBadge,
#lightbox,
#footer,
#beta,
#gamma,

.asset-footer,
#alpha-inner > ins,
#fb-root,
.zenback,
#trackbacks,
#comments
{ display: none !important; }

/* Display part */
body,
#container,
#container-inner,
#content,
#content-inner,
#alpha,
#alpha-inner,
#honbun,
.asset-content,
.entry-content
{
    width: auto !important;
    background: #ffffff !important;
    border: none !important;
    overflow: visible !important;
    float: none !important;
}

}
