css:
@-moz-document domain(www.gamefaqs.com) {
    #side_col_wrap {
        display: none !important;
    }
}
    /* Original CSS by Ricapar, edited to suit darker tastes by spynae to darkgreenclassic, edited again to newgreenbetter, then NGBVertical by BaalASystemLord */


@-moz-document domain(gamefaqs.com) {
    /* First we kill off the useless crap */

    #gne_nav,
    img[alt="madison"],
    #content + img,
    img[alt="advertisement"],
    img[alt="Click Here"],
    #footer,
    #aboutcnet,
    #logo,
    div[style="padding:10px 0px 0px 0px; float:left; clear:left;"],
    iframe,
    div[style="text-align:center"],
    #board_wrap + div,
    #searchbox label,
    #searchbox .btn,
    #board_wrap > div:first-child {
        display: none !important;
    }

    /* Basic Page Stuff */

    html,
    body {
        background: #000 !important;
        margin: 0 !important;
        padding: 0 !important;
        font-family: Arial, sans-serif !important;
        color: #CCC !important;
        font-size: 12.8px !important;
    }

    table {
        width: 100% !important;
    }

    p {
        margin: 0 !important;
    }

    /* Links */

    a {
        color: #BBB !important;
        text-decoration: none !important;
    }

    a:hover {
        color: inherit !important;
        background-color: inherit !important;
        text-decoration: underline !important;
    }

    table a:visited {
        color: #2DB300 !important;
    }

    #header a,
    #quicknav a,
    #copyright a,
    .user a,
    .pages a,
    .boardnavright a,
    .pagejumper a,
    #platformlist a,
    ul.content_nav a {
        color: #CCC !important;
        font-weight: bold !important;
        margin-left: 5px !important;
        text-transform: capitalize !important;
    }

    #header a:hover,
    #quicknav a:hover,
    .user a:hover,
    .pages a:hover,
    .boardnavright a:hover,
    .pagejumper a:hover,
    #platformlist a:hover,
    ul.content_nav a:hover {
        color: #BB2 !important;
        text-decoration: underline !important;
    }

    /* Header */

    #header {
        background: #009900 !important;
        color: #fff !important;
        text-align: right !important;
        padding: 10px 5px !important;
        font-weight: bold !important;
        margin: 2px !important;
    }

    #headerbottomright {
        display: none !important;
    }

    #quicknav,
    #searchbox {
        padding: 2px !important;
    }

    #quicknav {
        position: absolute !important;
        top: 3px !important;
        left: 5px !important;
        background: #009900 url('data:image/gif;base64,') no-repeat top left !important;
        padding-top: 30px !important;
        padding-left: 0 !important;
    }

    #quicknav a {
        margin-right: 8px !important;
    }

    #searchbox {
        color: #fff !important;
	padding: 2px !important;
        font-weight: bold !important;
        position: absolute !important;
        top: 2px !important;
        left: 150px !important;
        z-index: 5 !important;
    }

    #searchbox input,
    #searchbox select {
        width: 140px !important;
    }

    #platformlist,
    .crumbs,
    ul.content_nav {
        background: #008000 !important;
        padding: 2px !important;
        margin: 2px !important;
        overflow: hidden !important;
    }

    .crumbs {
        background: #000 !important;
        padding: 5px 7px !important;
    }

    #platformlist .nav {
        float: left !important;
        font-weight: bold !important;
        padding: 5px 4px 0 10px !important;
        color: #CCC !important;
    }

    #platformlist .jumper {
        float: right !important;
    }

    ul.content_nav {
        margin: 0 !important;
        padding: 0 !important;
        font-size: 15px !important;
        text-align: center !important;
    }

    ul.content_nav li,
    .pagejumper li {
        display: inline !important;
    }

    ul.content_nav li:not(:last-child):after {
        content: ' | ' !important;
        color: #fff !important;
        font-weight: bold !important;
    }

    /* Footer */

    #copyright {
        background: #000 !important;
        border-top: 2px #898989 dashed !important;
        color: #CCC !important;
        text-align: center !important;
        padding-top: 6px !important;
        font-size: 10px !important;
        margin: 6px !important;
        margin-top: 2em !important;
    }

    /* User Bar */

    .user,
    .pages,
    .boardnavright,
    .pagejumper,
    .searchtopics {
        display: block !important;
        padding: 2px !important;
        font-size: 15px !important;
        font-weight: bold !important;
        color: #CCC !important;
        background: #000 !important;
        text-align: center !important;
        margin: 2px 2px 0 2px !important;
    }

    .pagejumper {
        border-bottom:  2px #898989 dashed !important;
        margin-bottom: 1em !important;
    }

    .pagejumper a {
        text-decoration: underline !important;
    }

    .user {
        border-top: 2px #898989 dashed !important;
        padding-top: 1em !important;
        margin-top: 1em !important;
    }

    #gamefox-quickpost-title {
        background-color: #000 !important;
        font: bold 16px arial, sans-serif !important;
        margin: 0 2px 2px 2px !important;
        width: auto !important;
    }

    .pages {
        background: #000 !important;
    }

    .pagejumper {
        margin: 2px !important;
    }

    .searchtopics {
        margin-top: 0 !important;
    }

    .pagejumper ul {
        margin: 0 !important;
        padding: 0 !important;
    }

    .pagejumper li:not(:last-child):after {
        content: ' | ' !important;
    }

    /* Tables */

    td,
    th {
        padding: 2px !important;
    }

    th {
        background: #008000 !important;
        font-size: 15px !important;
        color: #DEDEDE !important;
    }

    table:not(.message) td {
        background: #333 !important;
    }

    /* Page Content */

    h1 {
        text-align: center !important;
        margin: 0 !important;
        padding: 3px !important;
        color: #CCC!important;
        font-size: 28px !important;
        font-style: italic !important;
    }

    .notice {
        padding: 2px 2px 1em 2px !important;
    }

    .notice h2 {
        color: #CCC !important;
        font-size: 13px !important;
        margin: 0 !important;
        text-transform: uppercase !important;
    }

    .notice span {
        margin: 2px 0 -2px 0 !important;
    }

    .error {
        color: #fff !important;
        background: #537F45 !important;
    }

    /* Messages */

    .message tr:not(.even) > td:not([style]):first-child,
    .message tr.even > .author:not([style]) {
        color: #CCC !important;
        background: #272727 !important;
    }

    .message tr > td:first-child:before {
        content: 'From: ' !important;
        font-weight: bold !important;
    }

    .message .even > td:last-child,
    .message tr:not(.even) > .author + td:not([style]):last-child,
    .author + td,
    .graybox {
        color: #CCC !important;
        background: #333333 !important;
    }

    .message .even > td:last-child:before {
        content: '' !important;
    }

    .message a.name + a,
    .message a.name + br + br + a {
        text-transform: capitalize !important;
    }

    .message td {
        vertical-align: top !important;
    }

    .author {
        width: 15% !important;
    }

    #board_wrap .head h1 {
        font-size: 16px !important;
        text-align: center !important;
        margin: 0 !important;
        padding: 3px !important;
        font-style: normal !important;
    }

    /* Post/Preview Page stuff */

    .subhead1 {
        display: block !important;
        background: #000 !important;
        padding: 2px !important;
        margin: 2px !important;
        color: #fff !important;
        font-weight: bold !important;
        font-size: 15px !important;
    }

    textarea {
        color: #fff !important;
        background: #333 !important;
    }

    textarea#gamfox-message {
        width: 100% !important;
        font: #FFF 12.8px arial !important;
        }

    input:not(#searchtextbox) {
        color: #fff !important;
        background: #333 !important;
    }

    #gamefox-quickpost-form {
        background: #000 !important;
    }
    /* Code thingers */
  /* <em><i></i></em> = <code> */
  .graybox em > i, 
  .message td > em > i {
    font-family: monospace;
    font-style: normal;
    display: block;
    white-space: pre;
    color: #AAA;
    display: block;
    overflow: scroll;
    background: #444;
    margin-right: 15%;
    margin-left: 15%;
    width: 70%;
    height: 30em;
    border: 1px #FFF dashed;
    }
  /* <i><em></em></i> = <q> */
  .graybox i > em:before, 
  .message td > i > em:before { 
    content: open-quote;
    }
  .graybox i > em:after, 
  .message td > i > em:after { 
    content: close-quote;
    }
  /* <strong><b></b></strong> = <h1> */
  .graybox > strong > b, .message td > strong > b {
    font-size: 200%;
    text-align: center !important;
    display: block !important;
    }
  /* <p><strong><em></em></strong</p> = <table><tr><td></td></tr></table> */
  .graybox > p:last-child br, .message td > p:last-child br {
    display: none;
    }
  .graybox > p:last-child, .message td > p:last-child {
    display: table;
    width: 40em;
    border-collapse: collapse;
    }
  .graybox > p:last-child > strong, .message td > p:last-child > strong {
    display: table-row;
    font-style: normal;
    font-weight: normal;
    }
  .graybox > p:last-child > strong > i, .message td > p:last-child > strong > i {
    font-style: normal;
    font-weight: bold;
    display: table-cell;
    color: #AAA;
    background-color: #444;
    }
  .graybox > p:last-child > strong > em, .message td > p:last-child > strong > em {
    display: table-cell;
    font-style: normal;
    font-weight: normal;
    border: 1px #EEE solid;
    background-color: #555;
    color: #BBB;
    }
}
/* addition of example theme by Spynae */
#searchbox div[class^="rubics"]
{ display: none !important }

/* I put this in to make the quick post box the same size as messages when using the vertical board setting. */
#gamefox-message { width: 99% !important; }
/* removal of reset button by Spynae */
input[type="reset"] { display: none; }
/* removal of Preview and Spellcheck Message button by Spynae */
input[value="Preview and Spellcheck Message"] { display: none; }
/* removal of Track Topic button by kirsybuu */
a[href$="action=tracktopic"]
{
display: none !important;
}
/*removal of logout button by MyDaddyHitsMe with help from TakatoMatsuki */
[href*="logout"] {display:none !important;}
/* help getting create new topic page back by TakatoMatsuki */
#board_wrap:not(#helloworld) .body {display:block !important;}
/* correct codes for game link navigation under board name provided by Poo Poo Butter */
