css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("washingtonpost.com")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer, fieldset, #footer

{
color: white !important;
background: black !important;
}

select, button, .container-fluid, #wpleaderboard, .top-bar, #shell, #footer-v3 div, #utility-wrapper, .module, .rollMe, .rollMeInner, .rollMeInner div, #page, .cf

{
background: black !important;
}

span, a

{
background-color: black !important;
}

select, input, span, code, textarea, pre, label, div, dd, option

{
color: white !important;
}


/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre, input, code

{
background: #333333 !important;
}

}
