css:
@namespace url("http://www.w3.org/1999/xhtml");

@-moz-document domain("port.hu") {
#above_menu_bar, #below_menu_bar, .banner, #fooldalflash, #link_, #link_ a, #fooldal_head_bg_mozaik, #sales-link, #leftTportal, #rightTportal, #cinema_main_page_right_2, td.th_right_box object, td.th_right_box embed, #right-banner-container, #ajklkslprswwlrnumlmokhhqbbb_swf_o, #fooldal_head_bg_mozaik {
display:none !important;
}

header.port-2-header nav {
  width: auto !important;
  white-space: nowrap !important;
  text-align: right !important;
  line-height: 48px !important;
  background: #3e3e3e !important;
}

header.port-2-header nav a {
  float: none !important;
  margin: 0 !important;
  position: relative !important;
  top: 0 !important;
  overflow: hidden !important;
}

header.port-2-header nav .logo {
  float: left !important;
  background-color: #ef3b3a !important;
}

section[data-role="content"] {
  width: auto !important;
}

.main-panel {
  width: 72% !important;
  margin-right: 0 !important;
  padding-right: 2% !important;
}

.side-panel {
  width: 26% !important;
}

.col-1,
.col-2,
.col-3 {
  -webkit-box-sizing: border-box !important;
  -moz-box-sizing: border-box !important;
  -ms-box-sizing: border-box !important;
  -o-box-sizing: border-box !important;
  box-sizing: border-box !important;
}

.col-2 {
  width: 50% !important;
  margin-right: 0 !important;
  padding-right: 2% !important;
}

.col-1 {
  width: 32% !important;
  margin-right: 1% !important;
}

div.x_loginholder.opened {
  right: 10px;
  left: auto;
  width: 290px;
}

.content-size {
  width: auto;
}

.title_header {
  background-size: cover !important;
}

div.x_mainframe {
  width: auto;
  margin: 0 auto;
  position: relative;
}

#endpage_main_box,
#endpage_side_box {
  -webkit-box-sizing: border-box !important;
  -moz-box-sizing: border-box !important;
  -ms-box-sizing: border-box !important;
  -o-box-sizing: border-box !important;
  box-sizing: border-box !important;
}

#endpage_main_box {
  width: 75% !important;
}

#endpage_side_box {
  width: 25% !important;
}

#vote_box {
  height: 250px !important;
  width: auto !important;
}

.title-container {
  display: block !important;
}

.title_header::after {
  /*background: none !important;*/
}

.title_header h1 {
  max-width: none !important;
  line-height: 50px !important;
  margin: 0 !important;
  left: 0 !important;
  padding: 0 20px !important;
  background-color: rgba(0, 0, 0, 0.2) !important;
  color: #fff !important;
}

.title_header h1 span {
  display: block !important;
  line-height: 20px !important;
  padding-bottom: 6px !important;
}

#endpage_main_box_content {
  width: auto !important;
}

.main-container {
  width: auto !important;
}

.separator {
  background: none !important;
  height: 0 !important;
  margin: 10px 0 !important;
  border-bottom: 1px solid #ccc !important;
}

.port2-list {
  width: 100% !important;
}

table a.buy-ticket {
  float: right !important;
  white-space: nowrap !important;
  top: 10px !important;
  margin-right: 10px !important;
}

.header-search-wrapper {
  width: 100% !important;
  left: 0 !important;
  -webkit-box-sizing: border-box !important;
  -moz-box-sizing: border-box !important;
  -ms-box-sizing: border-box !important;
  -o-box-sizing: border-box !important;
  box-sizing: border-box !important;
}
}
