@-moz-document domain("http://www.fear.fm"),
               url("http://www.fear.fm"),
               url-prefix("http://www.fear.fm/radio/player")
{
  #twitter, #ads
  {
    visibility: hidden;
  }
  html
  {
    background:url(http://fc09.deviantart.net/fs49/f/2009/159/2/c/FearFM_Harder_stream_by_pietbel.png) top left no-repeat !important; 
    background-color: #000000 !important;
    background-repeat:no-repeat !important;
    background-position:50% -60px !important;
  }
  #mainnav
  {
    background:#000000 !important;
  }
  #player
  {
        -moz-border-radius: 10px;
