  display: table !important ; color: #000000 !important;  vertical-align: top !important;
  text-transform: lowercase !important;  font-variant: small-caps !important ; font-weight: bold !important;
  background-position: 30px !important; background-repeat: no-repeat !important}

h1{font-size: 158% !important}
h2{font-size: 130% !important}
h3{font-size: 120% !important}
h4{font-size: 110% !important}
