css:
@-moz-document domain('netwars.pl'), url-prefix('http://netwars.pl/') {
.quote{
border: none;
background: none;
margin: 0 10px 10px 10px;
color: #CCC;
font-size: 10px;
}
.quote b{
font-weight: normal;
display: block;
}
.quote span {
padding: 5px;
background: #70360b;
display: block;
border-top: 1px solid #7a3d0c;
border-bottom: 1px solid #7a3d0c;
}
.quote span a.kategoria{
font-size: 10px !important;
}
.quote>br, .quote+br{
display:none !important;
}

.quote b a{
font-size: 9px;
}
textarea {
width: 480px;
font-family: Arial;
font-size: 13px;
}
td.kbody a.komentarzuser{
color: #823d0d!important;
font-size: 10px !important;
}
td.kbody a.komentarz {
color: #c41919!important;
font-size: 10px !important;
}
td {
position: relative;
}
td .numerek_posta+a+a.postHDL{
display: inline-block;
position: absolute;
top: 5px;
left: 328px;
font-size: 9px;
z-index: 1000;
}
}
