css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("friendfeed.com") {

* { font-family: ubuntu !important; }

body {background-color: #dfdfdf !important;}

body, .entry input, #footer select { font-size: 13px !important; }

.entry .info, .entry .likes, .entry .comments, .entry .bottomcomment, .entry .info a.date, .entry .info a.service, .entry .comments a.via, .entry .commentform textarea { font-size: 12px !important; }

.sharebox, .sharebox td { font-size: 13px !important; }

.sharebox textarea { font-size: 14px !important; }

ul.l_tolist li.l_tocard.public { font-size: 12px !important; }

.entry {
    /*display: inline-table  !important;
    height: 350px  !important;
    padding: 14px  !important;
    width: 315px !important;*/
}

div.entry:hover {
}

#body/*, #sidebar .box*/{
box-shadow: 0px 0px 4px #999999 !important;
border-radius: 10px !important;
}

img.picture.medium {
border-radius: 5px 5px 5px 5px !important;
}

#sidebar .box .box-bottom {
    box-shadow: -4px 5px 5px #BBBBBB !important;
}

#sidebar .box .section {
    border-bottom: 1px solid #CCCCCC !important;
}

}
