css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("webwork.math.ttu.edu") {
body{background:#001820!important;}

/*----------------  side panels ---------------------*/
#fisheye{
    background:#99ddff!important;
    border-color:#fff!important;
}

#fisheye h2{
  background-color:#00d5ff!important;
}
.viewOptions{
    background:#99ddff!important;
    padding:0 0 25px 0!important;
    margin:0 0 -25px 0!important;
}

#breadcrumbs{
    background:-moz-linear-gradient(left, #00d5ff, #0099aa, #004455)!important;
    border:none!important;
    -moz-border-radius:5px 5px 1px 1px !important;
    margin-bottom:-25px!important;
    padding-bottom:20px!important;
}



#content{
    background-color:#00aaaa!important;
    -moz-border-radius:0 0 2px 2px!important;
    border-top:2px solid #000!important;
}

.Body{
    background:transparent!important;
    margin:0 20px 0 20px!important;
}

.problem {
    background-color:#FFF!important;
    -moz-border-radius:10px 10px 2px 2px!important;
    margin:0 -10px -45px -10px!important;
    padding:1px 10px 50px 10px!important;
}


.attemptResults{margin:0 auto 0 auto!important;}

.ResultsWithError{
    background:-moz-linear-gradient(left, #00aaaa, #f00, #00aaaa)!important;
    color:#FBB!important;
    text-align:center!important;
    margin:0 50px 0 50px!important;
}


.ResultsWithoutError{
    background:-moz-linear-gradient(left, #00aaaa, #6f6, #00aaaa)!important;
    text-align:center!important;
    margin:0 50px 0 50px!important;
}

.attemptResultsSummary{text-align:center!important;}

/*
#last-modified{
    z-index:9001!important;
    background:url(http://oi51.tinypic.com/357q4i1.jpg)!important;
    height:1000px!important;
    width:710px!important;
    color:rgba(0,0,0,0)!important;
    position:fixed!important;
    top:70px!important;
    right:0!important;
    opacity: 0.4!important;
}
*/

/*------------------------------------ buttons -------------------------------------*/
.nav {height:30px!important;margin:-5px 0 0 0 !important;padding:0 0 0 10px!important;}
.nav a img {height:19px!important; width:0px!important; padding:0 71px 0 0!important;}

.nav a:nth-child(1){padding:0 0 0 3px !important; background:url(http://oi56.tinypic.com/10zwwzk.jpg) bottom left no-repeat!important;}
.nav a:nth-child(1):hover{padding:0 0 0 3px !important; background:url(http://oi53.tinypic.com/xf2sz6.jpg) bottom left no-repeat!important;}

.nav a:nth-child(2){padding:0 0 0 3px !important; background:url(http://oi53.tinypic.com/16h582g.jpg) bottom left no-repeat!important;}
.nav a:nth-child(2):hover{padding:0 0 0 3px !important; background:url(http://oi53.tinypic.com/znt2yb.jpg) bottom left no-repeat!important;}

.nav a:nth-child(3){padding:0 0 0 3px !important; background:url(http://oi56.tinypic.com/iof443.jpg) bottom left no-repeat!important;}
.nav a:nth-child(3):hover{padding:0 0 0 3px !important; background:url(http://oi54.tinypic.com/kajnlz.jpg) bottom left no-repeat!important;}

/*------------------------------------ input -------------------------------------*/

input[type="text"] {
    -moz-appearance: none !important;
    color: #000!important;
    border:#000 5px double;
    background: #f56!important;
    -moz-border-radius:2px!important;
}

input[type="submit"] {
    -moz-appearance: none !important;
    color: #67ffff!important;
    background: #2b2b2b!important;
    -moz-border-radius:1px!important;
    border-top:1px solid #777;
    border-left:1px solid #aaa;
    border-bottom:1px solid #222;
    border-right:1px solid #000;
    margin:0 3px 0 3px!important;
}

input[type="checkbox"] {
    -moz-appearance: none !important;
    color: #00aaff!important;
    -moz-border-radius:2px!important;
}

input[type="radio"] {
    background: #2b2b2b!important;
    -moz-appearance: none !important;
    color: #67ffff!important;
    -moz-border-radius:3px!important;
}

#logo {
    height:50px!important; overflow:hidden!important;
    background:-moz-linear-gradient(left, #006688, #003388)!important;
}

#logo img{
    background:url(http://oi53.tinypic.com/2ev9n2t.jpg)top left no-repeat!important;
    padding-top:70px!important;
}



}
