css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("stackoverflow.com"), domain("superuser.com"), domain("stackexchange.com"), domain("askubuntu.com") {
#header, #content, #mainbar-full, #wrap, .footerwrap{width: 1500px !important;}
#question-header{width: 100% !important;}
#header #hmenus{width: auto !important;}
#header{overflow: hidden;}
#mainbar{width: 1150px !important;margin-right: 0 !important;}
#question, #answers, #answers-header, .answer, .question-summary{width: 1135px !important;}
.user-stats-table .question-summary {width: 1205px !important;}
.post-text, .comments, .post-editor, .wmd-button-bar, .wmd-input, .wmd-preview, .question-status, .fw, #footer-menu{width: 1060px !important;}
.grippie{width: 1066px !important;}
.narrow .summary, .summary{width: 950px !important;}
#sidebar{width: 345px !important;}
}
@-moz-document domain("askubuntu.com"), domain("sharepoint.stackexchange.com") {
.container {width: 1500px !important;}
#header, .container{background-position: left top !important;}
#header #topbar{background: url("http://cdn.sstatic.net/askubuntu/img/bg-top-bar.png");}
.mainnavs{width: 570px;}
}
@-moz-document domain("blog.stackoverflow.com") {
section#content {width: 100% !important;}
header.main, .contentWrapper { width: 1500px !important;}
.commentGuests {width: 1135px !important;}
}
