;font-weight:bold;}#rightR ul li div.sl ul li a,#inner{color:white;}#stats .statsR a{text-decoration:underline;color:white;font-weight:bold;}#leftR h3,#rightR h3,#leftR ul li div,#rightR ul li div{background-color:#848684;border:0px;}#buy td p{padding:4px;margin:4px;}#inner a{color:white;text-decoration:underline;font-weight:bold;}input,select{background-color:#848684;color:white;text-align:center;border:1px solid white;}tr,#breed .holder .starter,#breed ul li div.roster,.eBox div,#configure li select,#configure li input,.dex .iBox div,#towers td div.i,.maintenance,.ranks th,.notice,#cBox,.dex div,.dex tr th,.dex td table td,#inner .r1,#inner .r0,#configure li p{background-color:#848684;color:white;}.success,.error{background-color:#848684;border:3px solid #fff;}.profileBox,#towers th{border:1px solid #fff;background-color:#848684;}.ld,input.center{border:1px solid #fff;color:white;background-color:#848684;}#buy td{background-color:#848684;border:1px solid #CCC;}#rightR ul li div.sl ul li a:hover{color:#848684;background-color:white;text-decoration:none;}tr.row0,tr.row0 td{color:white;border:1px solid white;}tr.row1,tr.row1 td{color:#848684;border:1px solid #848684;}table{border:0px;}li.pb,li.p0{color:#848684;}#footer_clear,#eggs li div,#profile li div.i{background-color:#848684;}#profile li div.h{color:white;}
