@namespace url(http://www.w3.org/1999/xhtml);







@-moz-document url-prefix(http://www.google),



               url-prefix(http://images.google) {



html {



background: #303231 url("http://www.papeldeparede.etc.br/wallpapers/musica--colcheia_6966_1024x768.jpg") center no-repeat !important;



background-attachment: fixed !important;



margin:0 !important; }



body {



-moz-border-radius: 8px !important;



border: 2px solid #707070 !important;



width: 84% !important;



min-width: 815px !important;



padding: 8px 1.2% 0 1.2% !important;



margin: 51px auto 30px auto !important; }



input[type=text], input[name=q] {



-moz-appearance: none !important;



border: solid 1px #4B4B4B !important;



-moz-border-radius: 6px !important;



background: #292a2b !important;



padding: 29px 12px 2px 12px !important; }



.lst { font-size: 14px !important; }



a:link, a, p, a:visited { text-decoration: none; }



a:visited, #gb a:visited, #guser a:visited, td.tc a:visited, td a:visited, p.a:visited, #res a.l:visited, #navbar a:visited { color: #c6c6c6 !important; }



a:hover, #navbar a:hover { color: #a2a2a2 !important; }



#header { padding-top: 0; }



tr[valign="top"] td table{ width: 100% !important; }



tr[valign="top"] td table input[type='text']{ width: 50% !important; }



input[name='q']{



outline: none;



padding:2px !important;



-moz-border-radius: 4px;



border: 1px solid #ccc !important; }



#gbar{



background-color: #292a2b;



border-bottom: 1px solid #5a5a5a; }



#gbar b.gb1, #gbar a.gb1:hover, a.gb2:hover, #gbar a.gb3:hover {



-moz-border-radius-topright: 4px !important;



-moz-border-radius-topleft: 4px !important;



border-bottom: 0 none !important; }



#gbar b.gb1 {



border: 1px solid #ea5757 !important;



color: #fff;



background-color: #8c0707; }



#gbar a.gb1:hover, a.gb2:hover, #gbar a.gb3:hover, #gb a:hover, #guser a:hover {



background-color: #07638C;



border: 1px solid #57BEEA !important; }



#gbar .gb1, .gb2, #gbar .gb3 {



line-height: 20px;



font-size: 12px;



border: 1px solid transparent;



border-bottom: 0 none;



text-decoration: none;



float: left;



padding: 0 5px 0 5px; }



#gb a, #guser a, td.tc a, td a, p.a, td.tc a:hover, td a:hover, p.a:hover { background-color: #111; }



#gb a, #guser a {



border: 1px solid #111 !important;



font-size: 95%; }



#gb a, #guser a, #gb a:hover, #guser a:hover { padding: 3px 4px 4px 4px; }



td.tc a, td a, p.a { font-size: 90%; }



.g, div.e, #tads {



-moz-border-radius: 8px;



overflow: hidden; }



.g, div.e {



padding:10px 10px 7px 12px !important;



border: 1px solid #585858;



background-color: #1F2122; }



#tads {



width: 110px;



height: 19px;



margin-top:5px;



margin-bottom:-8px;



margin-right: 1.2em;



padding: 0 10px!important;



border: 1px solid #D5DDF3; }



#tads a, #tads li, #tads h2 { padding: 0 !important; }



#tads h2, #ssb { font-size: 11px !important; }



#tads h2 { float: none !important; }



#tads h2:after, #rhsline h2:after { content: ' â–¼'; }



#tads:hover{



width: auto !important;



height: auto !important; }



.g:hover, div.e:hover{



background-color: #1b1d1e !important;



border: 1px solid #464646 !important; }



#mbEnd, #ImgContent table { position: relative; }



body, #gbar .gb1, .gb2, #gbar .gb3, #mbEnd { display: block; }



#mbEnd, #mbEnd:hover {



overflow: hidden !important;



background-color: #F7FFFF;



-moz-border-radius: 6px; }



#mbEnd {



margin-top:-34px;



height: 18px !important;



border: 1px solid #D5DDF3 !important;



margin-right:1.5%; }



#mbEnd:hover {



height: auto !important;



border: 1px solid #6B90DA !important; }



.g div.s cite {



font-size: 11px;



color: #2293c3 !important;



letter-spacing: 1px; }



.g div.s table.slk tr td div a, .g div.s table.slk tr td a.fl { background: none; }



#res a.l:visited { text-decoration:line-through; }



#res a.l:visited:after{ color: #c6c6c6; }



#res li:hover a.l:after { color:#0F3AB8; }



#ssb {



border:1px solid #6B90DA;



margin-right:1.1em !important;



overflow:hidden; }



#prs{ padding:4px 5px 0 10px !important; }



#ImgContent table { width: 99%!important; }



td[id^="tDataText"] { color: #bbb !important; }



td[id^="tDataText"] font[color="#008000"] { color: #777 !important; }



a#logo, a#logo[title*="Google Home"] { font-size: 0 !important; }



img[src*="/logos/"][width="276"], img[src$="logo.gif"], div[style*="logo_plain.png"], img[src$="logo_google_suggest.gif"],img[src$="images_hp.gif"], img[src$="images_logo_lg.gif"] {



margin-top: 3em !important;



width: 0 !important;



height: 159px !important;



padding-left: 450px !important;



/* background-image: url("http://i42.tinypic.com/zx8cwy.png") !important; */ }



#logo > img, #logo span, img[src$="logo_sm.gif"], img[src$="google_sm.gif"], img[src$="logo_google_suggest_sm.gif"], img[src$="images_logo.gif"], img[src$="nav_logo6.png"] {



width: 0 !important;



height: 51px !important;



padding-left: 135px !important;



background-position: 0 0 !important;



top: 0 !important;



background-image: url("http://i42.tinypic.com/zx8cwy.png") !important;



margin: 34px 12px 0 15px !important; }



img[src$="images_res.gif"], img[src$="logo_sm.gif"] {



width: 0 !important;



height: 61px !important;



padding-left: 136px !important; }



#sft #logo {



width: 135px !important;



height: 51px !important; }



#sft #logo img {



margin-top: 4px !important;



margin-left: 0px !important;



width: 400px !important;



height: 51px !important;



padding-left: 168px;



background: url("http://i42.tinypic.com/zx8cwy.png") no-repeat !important; }



#bsf, table.ft.t.bb.bt, #footer {



background: none !important;



margin-top: -40px !important; } 



#nav > tbody > tr > td span { background: none !important; }



#nav, #nav .cur, #navbar a { font-size: 120% !important; }



#nav { margin-top: -30px !important; }



#nav .cur { margin-top: -40px !important; }



#nav td, #navbar * { vertical-align: bottom !important; }



#nav td { padding: 0.15em !important; }



#navbar img { visibility: hidden !important; }



#navbar * {



background-image: none !important;



padding: 0.10em !important; }



#navbar .i { font-size: 1.2em !important; }



#navbar a { font-size: 0.9em !important; }



form p table[width="100%"][cellspacing="0"][cellpadding="0"][border="0"] {



border: 3px solid #585858 !important;



margin: 3px !important; }



form p table[width="100%"][cellspacing="0"][cellpadding="0"][border="0"] td[width="175"] { border-right: 1em solid #585858 !important; }



#gsr #res .g .s { max-width:100% !important; }



/* Update fade-in */



div#bsf, table#brs { display:none !important; }



div#ssb, #fctr, #sbl, span.fade, #guser, #fctr,#ghead,#pmocntr,#sbl,#tba,#tbe,.fade, td.k { background-color: transparent !important; }



/* thank's Silver the Paladin for Update fade-in */



/*********************************************************/



table.gac_m {



background-color: #000 !important;



opacity: 0.9 !important; }



.gac_b { background-color: #46534A !important; }



#rptgl span, .tl-sect div.tl b {



color: #fff !important; }



#rpsp.rpop, .tbo #tbd {



background-color: #333 !important;



border: 0 !important;



margin: 10px 0 0 50px !important;



padding: 8px 15px 0 5px !important;



-moz-border-radius: 4px !important; }



body { opacity: 0.65 !important; }



body, .t, table [bgcolor="#e5ecf9"], table [bgcolor="#d5ddf3"], .hd, td.ttb, td.rsb, td.bts, h4, .page-title, td#headerText, #ssb, span[style*="background: rgb(255, 255, 153)"], td[bgcolor="#d5ddf3"], tr[bgcolor="#cbdced"], tr[bgcolor="#bbcced"], tr[bgcolor="#ffffff"], tr[bgcolor="#E5ECF9"], form p table[width="100%"][cellspacing="0"][cellpadding="0"][border="0"], form p table[width="100%"][cellspacing="0"][cellpadding="0"][border="0"] td[width="175"], table[bgcolor="#ffffff"], tr[bgcolor="#ffffff"], tr[bgcolor="#cbdced"], h4, p > table > tbody > tr:first-child {



background: #111 !important; }



.gbh, #navbar br, form p table td[bgcolor="#cbdced"], span #botstuff {



display: none !important; }



body, input[type=text], input[name=q], a:link, a, p, a:visited, a:hover, #gb a:hover, #guser a:hover, td.tc a:hover, td a:hover, p.a:hover, .g:hover h3.r a, .g h3.r a, a:link, .w, #prs a:visited, #prs a:active, .q:active, .q:visited, #navbar .i, #navbar a, #navbar a:hover {



font-family: Verdana, Tahoma, Helvetica, Times !important; }



#gbar b.gb1, #gbar a.gb1, a.gb2, #gbar a.gb1:hover, a.gb2:hover, #gbar .gb1, .gb2, #gbar a.gb3, #gbar a.gb3:hover, #gbar .gb3, #gb a, #guser a, td.tc a, td a, p.a {



font-family: Verdana, Tahoma, Helvetica, Times; }



body, input[type=text], input[name=q], .g:hover h3.r a, .g h3.r a, #navbar .i, #navbar a, #rptgl span, #guser a, #guser a:hover, .g div.s cite, #res li:hover a.l:after  {



font-weight: bold !important; }



#gb a, #gb a:hover, #guser a, #guser a:hover, #ssb {



-moz-border-radius: 4px !important; }



body, input[type=text], input[name=q] { color: #f5f5f5 !important; }



#gbar a.gb1, a.gb2, #gbar a.gb3, #gb a, #guser a, td.tc a, td a, p.a, .g:hover h3.r a { color: #a7a7a8 !important; }



a:link, a, p, #gb a:hover, #guser a:hover, td.tc a:hover, td a:hover, p.a:hover, .g h3.r a, #gbar a.gb1:hover, a.gb2:hover, #gbar a.gb3:hover, .g, div.e, font.med, #res p a.p, div.s, font.p, #navbar .i, div, td, font[color="#000000"], td[id^="tDataText"] b, .w, #prs a:visited, #prs a:active, .q:active, .q:visited { color: #fff !important; }



font[color="#224499"] { color: #116DA6 !important; }



#gbar a.gb1, a.gb2, #gbar a.gb3 { font-weight: 700 !important; }



#gbar b.gb1, #gb a, td.tc a, td a, p.a { font-weight: 700; }



font.med, #res p a.p, div.s, #tads a, #tads li { font-size: 12px !important; }



font.p { font-size: 13px; }



img[style*="border: 1px solid"], td[id^="tDataText"] b, td[id^="tDataText"], td[id^="tDataText"] font[color="#008000"], .bb, .bt, div#footer {



font-size: 80% !important; }



#rhsline, img[style*="border: 1px solid"], #ssb, #bsf, #imgContent img, .bb, .bt { border: none !important; }



}
