css:
@-moz-document url(http://www.tumblr.com/),  
 
                url-prefix(http://www.tumblr.com/),   
 
                domain(www.tumblr.com)  
 
 , url("https://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {  
 
   
 
  #logo {height: 0 !important;
	width: 0 !important;
	padding-left: 210px !important;
	padding-top: 49px !important;
	background: url(http://i3.photobucket.com/albums/y90/insaneonyx/PhotoshopnGraphics/cracktime.png) no-repeat !important;}


body 
	{background-color:#FFFFFF;background: url('http://i3.photobucket.com/albums/y90/insaneonyx/PhotoshopnGraphics/bg-2-1.png') bottom left #000000 no-repeat fixed !important;}

*  
	{text-shadow:none !important;}
	


#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: rgba(0,0,0,0.2) !important;}	


#auto_pagination_loader
	{background:rgba(56,161,207, 0.00)!important;}
div.return_to_top_icon{	background: none !important;
	width: 0px !important;
	height: 0px !important;
	margin-top: 0px !important;
	opacity: .5 !important;
	border-bottom: solid 30px white !important;
	border-right: 15px solid transparent !important;
	border-left: 15px solid transparent !important; }	
	
	


.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) { 
	background: none !important;
	width: 0px !important;
	height: 0px !important;
	margin-top: 0px !important;
	border-bottom: solid 11px white !important;
	border-right: 11px solid transparent !important;
	border-left: 11px solid transparent !important; }
.selection_nipple:not([class="selection_nipple white"]) {
	border-bottom: solid 11px #666666 !important;}	
	

	
.selected .following,
.selected .followers,
.selected .settings,
.selected .posts,
.selected .messages,
.selected .drafts,
.selected .queue,
.selected .likes {
	color: rgba(000,000,000, 0.50) !important; 
	background: #666666 !important;}

	
	


a.explore_more_tags,
a.add_and_remove,
a.customize,
a.mass_editor
	{background-color:#666666 !important;  
	background-image:none !important;
	border:0px !important}

div.hide_overflow
	{font-size:.9;}

a.tag,
a.explore_more_tags,
a.add_and_remove
	{height:9px;}
a.tag
	{position:relative;top:-10px;}
.selected  .tag
	{height:14px;background:#666666 !important; 
	position:relative;top:0px;
	color:rgba(000,000,000, 0.80) !important;}
ul
	{-moz-box-shadow: 1px 1px 3px #666666 !important;}
	


div.gradient,
div.dashboard_nav_item, 
a.permalink
	{background-color:#000000 !important;
	opacity:.9; 
	background-image:none !important;
	border:0px !important}
.content.is_media, 
.mask.top, 
.mask.bottom {
	background: none !important; }
.post.photo_post .photo, 
.post.photo_post .video_thumbnail {
	border-radius: 16px !important;}
.post.photo_post .photo, 
.post.photo_post .video_thumbnail 
	{-moz-box-shadow: 1px 1px 3px #000000 !important;}



blockquote
	{margin-left:-5px !important;}



.login.sign_up,
A[href="/forgot_password"] {display: none !important;}	



.mceContentBody {
  background: none !important; }
  


ol#posts li.notification
	{background-color: #6b6a65 !important;
	border-bottom: 2px solid #666666 !important;
	color: #000000 !important;}
	
#right_column a#dashboard_switch_blog_menu_current:hover 
	{background:#6b6a65;}
	
#nav .nav_item.active
	{background-color:#6b6a65;}
	
#right_column
	{color:#666666;}
	
#left_column #posts .post .permalink
	{background-color:#6b6a65;}

#nav .nav_item.active, 
#nav .nav_item .nav_menu, 
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel
	{background-color:#6b6a65 !important;
	background-image:none !important;
	border:0px !important}

#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current
	{background-image:none !important}

.notification_type_icon
	{display:none !important}

#left_column #posts .notification 
	{background-color:#6b6a65 !important;
	color:#000 !important;
	border-bottom-color: #6b6a65 !important}

#new_post_notice_container
	{color:#fff !important}

#new_post_notice_container a
	{color:#fff !important}

}
