@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.gonintendo"),
url-prefix("http://gonintendo") {

/* MAD DEV */
/* initiated by trustybellts */ 

/* Background */

#page, body, html  {background: url("http://techuff.webs.com/images/bg.png") repeat fixed left center !important;}

/* Top Header */

.topper-header {background-color: #131313 !important; color: #117099 !important; border: 0px solid #131313 !important;
      -moz-border-radius: 15px !important;
      -webkit-border-radius: 15px !important; background-image: -moz-linear-gradient(center top , #303030, #131313) !important; color: #119099 !important;}

/* Main Topper */

.clearfloat {background: transparent !important;  border: 0.5px solid transparent !important;
      -moz-border-radius: 15px !important;
      -webkit-border-radius: 15px !important;}
#mainnav { background-image: -moz-linear-gradient(center top , #303030, #131313) !important; color: #117099 !important; } 

/* Text Color */

a {color: #117099 !important}
a:visited, a:hover {color: #fff !important}
.featured-story, .topper-content {color: #fff !important}
h2 {color: #e7e7e7 !important}
p {color: #fff !important}
.story-rightcol, .comment-body cite, #comments .header, li.row strong, .postprofile strong, dd label, label {color: #fff !important}
#rightcol h3, .icon, .content, .story-body, .comment-body, .postprofile dd, .postprofile dt {color: #e7e7e7 !important}

.inner span {color: #117099 !important}


/* Featured */

.featured-story img {border: 0.5px solid transparent !important;
      -moz-border-radius: 15px !important;
      -webkit-border-radius: 15px !important;border-top-color: transparent !important}

/* Borders */

.story, .curved {border-top-color: #117099 !important;}

#communitystats, #topstoriesataglance, #recentchatter, #userreviews {border-left-color: #e7e7e7 !important;}
.topper-content {border-bottom-color: transparent !important}

fieldset#similarstories, fieldset.gamedb {border-top-color: #117099 !important; border-bottom-color: #117099 !important; border-right-color: transparent !important; border-left-color: transparent !important}

#similarstories legend, .gamedb legend {background-image: -moz-linear-gradient(center top , #303030, #131313) !important; color: #117099 !important;}

#rightcol h3 {border-bottom-color: #117099 !important; -moz-border-radius: 5px !important;
      -webkit-border-radius: 15px !important;}

/* Body */ 

.story-body {border: 2px solid transparent !important;
      -moz-border-radius: 10px !important;
      -webkit-border-radius: 15px !important; border-right-color: transparent !important; line-height: 1.5em !important}

.story-rightcol {border: 0.8px solid transparent !important;
      -moz-border-radius: 15px !important;
      -webkit-border-radius: 15px !important; background-image: none !important; border-top-color: #117099 !important;}

li.ss_li:hover {background-color: transparent !important}

#comments, .curved, .panel {background-color: #303030 !important}

/* Menu */

.first-active, .second, .first, .third, .fourth {background-image: -moz-linear-gradient(center bottom, #131313, #262626) !important; color: #117099 !important; border: 0px solid transparent !important;
      -moz-border-radius: 10px !important;
      -webkit-border-radius: 15px !important;}

.active-link {background-image: -moz-linear-gradient(center top , #303030, #131313) !important; color: #117099 !important; border: 0px solid transparent !important;
      -moz-border-radius: 10px !important;
      -webkit-border-radius: 15px !important;}

.first:hover, .second:hover, .first:hover, .third:hover, .fourth:hover {background-image: -moz-linear-gradient(center top , #303030, #131313) !important; color: #117099 !important; border: 0px solid transparent !important;
      -moz-border-radius: 10px !important;
      -webkit-border-radius: 15px !important;}

.nav-child {background-color: #303030 !important; border: 0px solid transparent !important;
      -moz-border-radius: 10px !important;
      -webkit-border-radius: 15px !important;}

.nav-parent:hover {background-image: -moz-linear-gradient(center top , #131313, #262626) !important; border: 0px solid transparent !important;
      -moz-border-radius: 10px !important;
      -webkit-border-radius: 15px !important;}
.nav-child:hover {background-color: #131313 !important}

/* Message Boards */

.inside, .corners-top span, .corners-bottom span  {background-image: -moz-linear-gradient(center top , #131313, #303030) !important}

.border-bottom span {background-image: -moz-linear-gradient(center top , #303030, #131313) !important}

#wrap {background-color: #131313 !important;  border: 0px solid transparent !important;
      -moz-border-radius: 10px !important;}
.navbar, .forabg, .row {background-image: -moz-linear-gradient(center top , #303030, #131313) !important}

.bg2, .bg3 {background-color: #303030 !important;}
.bg1 {background-color: #131313 !important;}

blockquote {background-color: #131313 !important; border: 0px solid transparent !important;
      -moz-border-radius: 10px !important;
      -webkit-border-radius: 15px !important;}

/* Games */

.odd {background-color: #131313 !important; border: 0px solid transparent !important;
      -moz-border-radius: 10px !important;}

.pagination a, .pagination span, .button2, .button1 {background-image: -moz-linear-gradient(center top , #303030, #131313) !important; color: #117099 !important; border: 0px solid transparent !important;
      -moz-border-radius: 10px !important;
      -webkit-border-radius: 15px !important;}

.pagination a:active, .pagination span, .button2:active, button1:active, .pagination span strong {background-image: -moz-linear-gradient(center top , #131313, #303030) !important; color: #117099 !important; border: 0px solid transparent !important;
      -moz-border-radius: 10px !important;
      -webkit-border-radius: 15px !important;}

/* Staff&info */

.staffinfo {background-image: -moz-linear-gradient(center top , #131313, #303030) !important; color: #117099 !important; border: 0px solid transparent !important;
      -moz-border-radius: 10px !important;
      -webkit-border-radius: 15px !important;}

/* Footer */
#footer {background-image: -moz-linear-gradient(center top , #131313, #303030) !important; color: #117099 !important;}

}
