css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kenrockwell.com") {
  font[face] {
    font-family: "Frutiger Linotype" !important;
  }
  font[size="+2"] {
    background-color: #fc4;
    padding: 1ex 1ex 0.5ex 50px;
    margin-top: 5ex !important;
    min-width: 110%;
    font-size: 22pt !important;
    position: relative;
    left: -50px;
    display: block;
  }
  font[size="+1"] {
    border-left: 25px solid #ff4;
    padding: 1ex 1ex 1ex 12px;
    position: relative;
    left: -37px;
  }
  p font[face="Courier New, Courier, monospace"] {
    font-family: "Monaco" !important;
    background: #f7f7f7;
    display: block;
    padding: 1ex 2ex 1ex 2ex;
  }
  table[width="600"] {
    min-width: 80% !important;
    width: 80% !important;
    position: absolute !important;
    left: 50px;
    padding: 0 !important;
  }
  #adds {
    display: none;
  }
  body {
    
    font-family: "Frutiger Linotype" !important;
    margin: 0 !important;
  }
  a[href] {
    text-decoration: none;
    color: blue;
  }
}
