@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("facebook.com") {

/* Author: Day Milovich */ 
/* Facebook Community: http://facebook.com/daymilovich */ 
/* Facebook Profile: http://facebook.com/tamanmerah */
/* Blog : http://daymilovich.blogspot.com */ 
/* https://plus.google.com/105205068969258745020 */
/* Website: http://vetostudio.info */

/* fonts */
*,html,body,span,div,td,br,input,font,button,select,option {font-family: "Segoe UI",Calibri !important;font-size: 12px !important;}
/* ------- */
/* specifies width of web search results column */
td.j {width: 75% !important;}
/* minimalistic.login */
.fbIndex { background-color: #3B5998 !important }
.fbIndex #globalContainer #dropmenu_container,
.fbIndex #globalContainer #content,
.fbIndex #globalContainer #pageFooter {display: none !important}
.fbIndex .loggedout_menubar_container {position: fixed !important;width: 420px !important;height: 82px !important;min-width: 0 !important;top: 50% !important;left: 50% !important;margin-top: -17px !important;margin-left: -210px !important;z-index: -1 !important;}
.fbIndex .loggedout_menubar { width: auto !important}
.fbIndex .loggedout_menubar_container .lfloat,
.fbIndex .loggedout_menubar_container .rfloat {float: none !important}
.fbIndex .loggedout_menubar_container .lfloat img,
.fbIndex .loggedout_menubar_container .rfloat #login_form table {display: block !important; margin: 0 auto !important}
.fbIndex .loggedout_menubar_container .lfloat img { display: block; margin: -70px auto 20px !important;}
#SetAsHomepage_Callout {display: none;}
/* ------- */
/* header fixed */
#blueBar {position: fixed !important; }
/* ------- */
/* single news feed, classic */
.highlightMenuItem, 
#recent_header, 
#highlight_header, 
#stream_movable_header, 
.uiStreamHeaderTall, 
.uiStreamSeparator, 
.highlightIndicator {display:none !important;}
/* ------- */
/* Reset font size */
.uiStream .uiStreamMessage,.uiStreamMessage .messageBody {font-size:11px!important;}
/* ------- */
/* no ads and get rid of off */ 
#ego,
#highlights_ad,
#home_sponsor,
#home_sponsor_nile,
#home_statusupdates,
#pagelet_rhc_ticker,
#pagelet_ego_pane, 
#presence,
#pymk_hp_box,
#rooster_container,
#sidebar_ads,
#ssponsor,
#status_sidebar,
.ad_capsule,
.ad_media,
.app_install_story,
.app_install_story,
.app_story,
.approve_friend,
.comments_ad_image,
.comments_add_box_image
.ego_column,
.ego_spo,
.fbnew_preview_bar,
.fbpage_fan,
.fbTimelineSideAds, 
.findfriends,
.footer_ad,
.friend_finder,
.gifts_received,
.hp_connect_box,
.invite_friends,
.invitefriends,
.inYOfac3Book,
.nextstep,
.profile_sidebar_ads,
.pymk,
.reviews_create,
.secondary_actions,
.sidebar_item sponsor,
.social_ad,
.sponsor,
.sponsors,
.UIConnectWithFriends,
.UIContextualFlag_Container,
.UIContextualFlag_HasSideArrow,
.UIRooster_Container,
.UIRooster_Content,
 .UIUpcoming_Info > .UIUpcoming_Time + small,
.wall_contextual_extra{display:none!important}
/* ------- */
}
