css:
@-moz-document
	url("chrome://brief/content/brief.xul"),
	url("resource://brief-content/feedview.html"){

body{	background:url('http://i.imgur.com/Iltk1.jpg') repeat-x left top fixed !important;	}
		#feed-view-header { background: #f2f2f2 !important;}
		
		
		
		
		article, .full-container {
	border:none!important;
	margin:auto!important;
	margin-top:0px!important;}

	
.full-container {
	background:	-moz-linear-gradient(top, rgba(70,35,15,.8)27px, rgba(100,50,20,.6)60px, rgba(100,50,20,.5)), url('http://i.imgur.com/CHTYd.png') no-repeat right top!important;
	width:700px!important;
	padding:		0px 2px 2px 2px					!important;
	margin-top:		10px							!important;
	box-shadow:		3px 3px 15px rgba(10,10,10,.9)	!important;
	border-radius:	15px							!important;
	border-style:	solid							!important;
	border-width:	1px								!important;
	border-color:	rgba(255,230,230,0.1)			!important;}

.full-container-inner {
	border:none!important;
	background:	none!important; 
padding:0!important;}		
		


/* Title */
	.title-link,#main-message { color: #84A50E!Important;	text-shadow: rgb(10,10,10) 0px 0px 4px!important; }
		
/* Title Gombok */
	.bookmark-button	{background: url(http://i.imgur.com/aBBpBJ4.png) no-repeat; background-position: 11px 0px}
	.mark-read-button	{background-image: url("http://i.imgur.com/dmdm6sv.png")}
	.delete-button		{background-image: url("http://i.imgur.com/1FdXYJ0.png")}
		

/* Sub Title -> Animeaddicts.hu */
	.subheader {color:#D59423!Important; text-shadow: rgb(10,10,10) 0px 0px 10px!important;}
	.feed-name {color:#D48700 !Important;}
	.date {margin-right:10px!important}

/* Article */
	.content a {color: #D17D3C!important;}
	.content, article.read .title-link, p {
		color: rgb(230,230,230)!Important;
		text-shadow: rgb(10,10,10) 0px 0px 5px!important;}





	.content > IMG,
	.content > P > IMG,
	.content > P > object 
		{display: block!important;	margin-left: auto!important;	margin-right: auto!important;}

/*comment:com*/		.item_ctp,
/*Casual Stroll*/	.feedflare
		{display:none!important}
		
		
		


}
