css:
@-moz-document domain("gcsecomputing.org.uk") {
body, table, table div, .MainHead , .ol_candidates, .style2, .style4, .style1, .style7    {
font-family: arial, helvetica, sans-serif !important;
}

table {
margin-top: 10px  !important;
}
table, table th, table td, .MainHead {
line-height: 1.5em  !important;
color: #000000 !important ;
border: none !important; //Removes annoying maroon border
}

table table, table table th, table table tr, table table td {
border: 1px solid #555 !important;

}

.cssnav span {
font-weight: bold  !important;
}

.hr {
border: 0  !important;
color: #CCC  !important;
background-color: #CCC  !important;
height: 1px  !important;
width: 100%  !important;
text-align: left !important;
margin-bottom: 10px !important;
}

table td {
background: none !important;
}

th a img, .cssnav_s img, .cssnav_r img, .cssnav img {
display: none !important;
}

.cssnav_s , .cssnav_r , .cssnav  {
background: #fff !important;
text-align: left !important;
}

.cssnav span {
position: relative !important;
cursor: default !important;
}

.current_button_text, .style6 , .style7, .style8 {
color: maroon !important;
}

.button_text, .style2, .style4, .sub_head, .style4  .style7   {
color: #000 !important;
}

.style7 img, .style8 img {
//display: none;
}
.style10 , a {
color: navy !important;
font-weight: normal !important;
line-height: 1.5em !important;
}
.sub_sub_head {
color: maroon !important;
}

.style12 {
color: #666 !important;
}
.ol_candidates {
color: #666 !important;
}

table tr th div.cssnav:first-child a .button_text{
font-size: 12px !important;
color: maroon !important;
font-weight: bold !important;
}

tr th>a {
/*background: url(http://www.ocr.org.uk/download/ict_header.jpg) no-repeat top right !important;
height: 60px !important;
width: 100% !important;
float: left !important;*/
}

}
